style.css:
@-moz-document domain("stackexchange.com"), domain("stackoverflow.com"), domain("serverfault.com") {
#content {
    width: 100%;
    border: none;
}

#mainbar,
.mainbar {
    width: 100%;
}

.community-option,
#sidebar,
#left-sidebar,
.bottom-notice {
    display: none;
}

.top-bar._fixed { 
    position: absolute; 
}
}
