style.css:
@-moz-document url-prefix("https://pluralistic.net/") {
.site {
    background-color: #333;
    color: #eee;
}

.site-branding .site-title a {
    color: yellow;
}

.site-branding .site-description {
    color: #fff;
}

a,
a:link,
a:visited {
    color: yellow;
}

h1.toch1 {
    color: #fff;
}

ul.toc li, .xToC {
    color: #eee;
}
}
