/* ==UserStyle==
@name           81Dojo Piece Contrast
@namespace      github.com/ddugovic/UserStyles
@version        1.0
@description    Contrasts pieces of each player.
@author         Daniel Dugovic
==/UserStyle== */
@-moz-document domain("81dojo.com") {
div[class^="square"][style*="G"][style*='.png'] {
    filter: contrast(200%)
}
div[class^="square"][style*="S"][style*='.png'] {
    filter: invert(100%)
}

div[class$="square-last"] {
    box-shadow: 0px 0px 0px 2px #00FF00 inset;
}
div[class$="square-last"][style*="S"][style*='.png'] {
    box-shadow: 0px 0px 0px 2px #FF00FF inset;
}
}
