style.css:
@-moz-document domain("crank.js.org") {
:root {
    --theme-color: #dbb368;

    --text-main: #000000;
    --text-header: var(--theme-color);
    --text-active: var(--text-header);
    --background-main: #ffffff;
    --background-code: #eeeeee;

    --border-color: #a8a8a8;
}

body {
    background: var(--background-main);
    color: var(--text-main);
}

h1,
h2,
h3,
.sidebar h3,
.feature h3 {
    color: var(--text-header);
}

.navbar,
.sidebar {
    border-color: var(--border-color);
}

:not(pre) > code {
    background-color: var(--background-code)
}

a.current {
    color: var(--text-active);
}

.hero a {
    color: var(--theme-color);
    border-color: var(--theme-color);
}

.hero h2 {
    color: var(--theme-color);
    filter: brightness(110%);
}
}
