style.css:
@-moz-document domain(downloads.khinsider.com)
{
#audio1 {
	display: unset;
	margin: auto;
}

.audioplayer {
	display: flex;
	flex-direction: row;
}

.audioplayerPlayPause,
.audioplayerTimeCurrent,
.audioplayerBar,
.audioplayerTimeDuration,
.audioplayerVolume,
.audioplayerVolumeBar {
	display: none;
}

#audioplayerCurrentSong {
	display: unset;
}
}
