style.css:
@-moz-document url-prefix("https://finobe.com") {
body {
		background: #f0f0f0 url(https://finobe.com/img/bgpat5.svg) no-repeat 50% fixed !important;
		-webkit-background-size: cover!important;
	}
	.navbar-light.bg-faded {
		background: #fff!important;
		border-bottom: 1px solid #e6e6e6!important;
	}
	.navbar-light .navbar-brand {
		color: black!important;
	}
	.navbar-light .navbar-nav .nav-link {
		color: rgba(0,0,0,.5);
	}
	.navbar-light .navbar-nav .nav-link:focus, .navbar-light .navbar-nav .nav-link:hover {
		color: rgba(0,0,0,.7);
	}
	.nav-scroller.navbar-light.bg-faded {
		display: none!important;
	}
	.home-main-content {
		background: url("https://finobe.com/img/hallo.png") no-repeat center center;
		background-size: cover;
	}
	.home-avatar-img {
		display: none!important;
	}
	.navbar-nobelium-dropdown .dropdown-header {
		padding-top: 5px;
		padding-bottom: 5px;
		padding-left: 0px;
		padding-right: 0px;
	}
	.navbar-nobelium-dropdown .dropdown-item {
		border-radius: 0px!important;
		padding: 1px;
		margin-bottom: 0px!important;
	}
	.btn-theme {
		color: #212529;
		background-color: white;
		border-color: #E6E6E8;
	}
	.card-header {
		border-bottom: 1px solid #E6E6E8;
	}
	.btn {
		text-transform: lowercase;
	}
	.fa-play::before {
		display: none;
	}
	.catalog-card > .card {
		box-shadow: 0 1px 3px rgba(0,0,0,.12),0 1px 2px rgba(0,0,0,.24);
		transition: all .3s cubic-bezier(.25,.8,.25,1);
	}
	.catalog-card > .card:active, .catalog-card > .card:hover {
		box-shadow: 0 3px 6px rgba(0,0,0,.16),0 3px 6px rgba(0,0,0,.23);
	}
	.footer .dropdown {
		display: none;
	}
    .footer b {
        visibility: hidden;
    }
    .footer b:nth-child(1)::before {
        content: "Copyright © Finobe 2017 | Codename: Pei Ho Street";
        visibility: visible;
    }
    .footer p:nth-child(1) {
        visibility: hidden;
    }
	.breadcrumb {
		background-color: #e9ecef;
	}
	.navbar-light .navbar-nav .show > .nav-link {
		color: black;
	}
	.btn:hover {
		box-shadow: 0 2px 5px 0 rgba(0,0,0,.16),0 2px 10px 0 rgba(0,0,0,.12)!important;
		transform: translateY(-1px);
	}
    .navbar-brand img {
        content:url("https://finobe.com/img/BUSY.png");
    }
}

@-moz-document url("https://finobe.com/") {
h1[class="mb-4 home-text"] {
    visibility: hidden;
}

p[class="home-text"] {
    visibility: hidden;
}

h1[class="mb-4 home-text"]::before {
    content: "We make old new again";
    visibility: visible;
}

p[class="home-text"]::before {
    content: "Finobe's goal is to emulate an old brickbuilding game.";
    visibility: visible;
}

h3[class="mb-2"] {
    display: none;
}

.fa-discord::before {
    color: #7289DA;
}
.fa-youtube::before {
    color: #E42B26;
}
.fa-twitter::before {
    color: #1DA1F2;
}
.container .game-card-div {
    display: none!important;
}
}

@-moz-document url-prefix("https://finobe.com/catalog"), url-prefix("https://finobe.com/users") {
nav.finobe-basic-nav > .header, nav.settings-nav > .header {
		background: #F5F7F9;
		border-bottom: 1px solid #d8d8da;
		padding: 10px;
		margin-bottom: 20px;
	}
	nav.finobe-basic-nav > .link, nav.settings-nav > .link {
		padding-left: 20px;
	}
	nav.finobe-basic-nav, nav.settings-nav {
		height: 300px;
		background: white;
		border: 1px solid #E6E6E8;
		border-radius: 5px;
	}
	nav.finobe-basic-nav > .link, nav.settings-nav > .link, nav.finobe-basic-nav > .link.active, nav.settings-nav > .link.active {
		color: #0381bf;
	}
	.form-control {
		border-top-right-radius: 0.25rem!important;
		border-bottom-right-radius: 0.25rem!important;
		margin-right: 10px;
	}
	.input-group {
		width: 300px;
		margin: auto;
	}
	.input-group .btn-secondary {
		background-color: #039be5;
		border-color: #039be5;
		width: 60px;
		border-top-left-radius: 0.25rem!important;
		border-bottom-left-radius: 0.25rem!important;
	}
	.form-inline.text-center {
		margin: unset;
	}
    button i {
        display: none!important;
    }

    button::before {
        content: "  search  ";
    }

    button {
        width: auto!important;
    }

    #catalog-type-selector {
    height: auto!important;
    padding-bottom: 2.5rem;
    }

    #catalog-type-selector .header {
        padding-top: .75rem;
        padding-bottom: .75rem;
        padding-left: 1rem;
    }

    #catalog-type-selector a {
        display: list-item;
        list-style-type: disc;
        list-style-position: inside;
        padding-left: 2.4rem;
    }

    button[data-target="#filtermodal"] {
        display: none;
    }

    .badge-warning{
        color: white;
    }
}

@-moz-document url-prefix("https://finobe.com/catalog") {
    .card-body {
        background-image: linear-gradient(bottom, #f7f7f9 25%, #ffffff 50%);
        background-image: -o-linear-gradient(bottom, #f7f7f9 25%, #ffffff 50%);
        background-image: -moz-linear-gradient(bottom, #f7f7f9 25%, #ffffff 50%);
        background-image: -webkit-linear-gradient(bottom, #f7f7f9 20%, #ffffff 20%);
        background-image: -ms-linear-gradient(bottom, #f7f7f9 50%, #ffffff 50%);
        padding-bottom: .8rem;
    }
    
    p[class="my-0 text-muted catalog-no-overflow-plz"] {
        padding-bottom: .6rem;
    }
    
    p[class="my-0 catalog-no-overflow-plz"] {
        padding-top: .8rem;
        border-top: 1px solid #d8d8da;
    }
}

@-moz-document url-prefix("https://finobe.com/forum/post?id=") {
.badge.badge-admin {
		color: #ff5252 !important;
		font-size: 13px !important;
		background-color: transparent;
		animation: none;
	}
	.badge.badge-event {
		color: #ffb300 !important;
		font-size: 13px !important;
		padding: 0px;
		background-color: transparent;
		animation: none;
	}
    .forum-voter {
        display: none!important;
    }
}

@-moz-document url-prefix("https://finobe.com/forum/home") {
.forum-vote-total {
    display: none;
}

div[class="text-center form-inline"] {
    float: left;
}
}

@-moz-document url-prefix("https://finobe.com/place/") {
.finobe-tabs {
    margin-top: 0!important;
    visibility: hidden;
}

.col-md-3 {
    padding: 0;
}
}
