style.css:
@-moz-document domain('bitbucket.org') {
  .type-add[data-qa="code-line"] {
    background: rgb(165, 255, 168) !important;
  }
  .type-add[data-qa="code-line"] ins {
    background-color: rgb(123, 241, 149) !important;
  }
  .type-del[data-qa="code-line"] {
    background: rgb(245, 170, 170) !important;
  }
  .type-del[data-qa="code-line"] del {
    background: rgb(220, 153, 153) !important;
  }
}
