style.css:
@-moz-document domain("twitter.com") {
/* Hide Notification Badge */
/* div[aria-live="polite"] {
    display: none;
} */

/* Hide Numbers in Timeline */
div[data-testid="reply"] > div > div:nth-child(n+2),
div[data-testid="retweet"] > div > div:nth-child(n+2),
div[data-testid="unretweet"] > div > div:nth-child(n+2),
div[data-testid="like"] > div > div:nth-child(n+2),
div[data-testid="unlike"] > div > div:nth-child(n+2) {
    visibility: hidden;
}

/* Hide Tweet Activity in Tweet Detail Screen */
/* a[href$="/analytics"] {
    display: none;
} */

/* Hide Numbers in Tweet Detail Screen */
article[role="article"] a[href$="/retweets"],
article[role="article"] a[href$="/likes"],
article[role="article"] a[href$="/retweets/with_comments"] {
    display: none;
}

/* Hide Replies in Tweet Detail Screen */
