div[aria-label="タイムライン: 会話"] > div > div > div:nth-child(n+2) {
    transition: opacity 0.3s ease-out;
    opacity: 0;
}
div[aria-label="Timeline: Conversation"] > div > div > div:nth-child(n+2):hover,
