style.css:
@-moz-document domain('forums2.cubiccastles.com') {
    /* Mod discussion comments styling */
    /* As old forums used to have */
    .Item.Role_ForumModerator, .Item.Role_GameModerator {
        background-color: #00a6ff10;
    }
    /* Admin discussion comments styling */
    /* As old forums used to have */
    .Item.Role_ForumAdministrator, .Item.Role_GameDeveloper {
        background-color: #ff000010;
        background-image: url('https://forums2.cubiccastles.com/uploads/AdminBannerFinal.png');
        background-repeat: no-repeat;
        background-position: right bottom;
    }
    /* Makes unread discussions stand out */
    .ItemDiscussion.Read {
        opacity: .7 !important;
    }
    /* No image overflow on activity wall */
    .ItemContent.Activity img {
        max-width: 100%;
    }
    /* Makes usernames more visible */
    a.Username {
        font-size: 15px !important;
        font-weight: bold !important;
    }
    /* Activity comments separator */
    .Item.ActivityComment {
        border-top: 1px solid #ffffff20 !important;
    }
    /* Dark comment box */
    textarea.InputBox, textarea.TextBox {
        background: #121212;
        color: #fff;
    }
    /* Hides unnecessary elements */
    .editor-help-text, .ImageUploadWrapper, .MItem.AuthorLocation, .Visits, .plupload.html5 {
        display: none !important;
    }
}
