style.css:
@-moz-document domain('www.instagram.com'), regexp('(.*)\.instagram\.(.+)\/.+') {
/* instagram */
body
{
    overflow: auto !important;
    height: 100% !important;
}

div[background-color="rgba(0,0,0,.8)"],
div[z-index="-9999"],
div[role="presentation"],
div[role="dialog"],
div[style="width: 100%;"]
{
	visibility: hidden !important;
	display: none !important;
}

}
