style.css:
@-moz-document url-prefix("https://www.pillowfort.social/") {
/* Post */
.post.main,
.header {
    border-top-left-radius: 1em;
    border-top-right-radius: 1em;
}

div.media img {
    border-radius: 1em;
}

.post-nav .nav-tab,
.post-nav-left {
    border-radius: 1em;
}

.post.main {
    border-bottom-left-radius: 1em;
    border-bottom-right-radius: 1em;
}

.avatar img,
.timestamp.ng-binding {
    border-radius: 0.6em;
}

.flag-button {
    border: none;
    border-top-right-radius: 0px !important;
}

.tags {
    font-weight: bolder;
}

.tags .ng-scope {
    font-weight: normal;
}


/* Sidebar */
.sidebar {
    border: 1px solid rgba(0, 0, 0, 0);
    border-top-right-radius: 0.6em;
    border-bottom-right-radius: 0.6em;
    height: auto !important;
}

.sidebar-icon {
    border-radius: 0.3em;
}

.sidebar-toggle-indicator {
    border-top-right-radius: 0.6em;
}

#searchme {
    border-bottom-right-radius: 0.6em;
}
}
