style.css:
@-moz-document url-prefix("https://www.skyscrapercity.com/threads/") {
.message-avatar .avatar--default
{
  display: none;
}

.message-avatar-wrapper, .avatar-badge-wrapper
{
  display: inline;
}

/* For narrow screens */

.message:not(.message--forceColumns) .message-avatar .avatar:not(.avatar--default) {
  margin-right: 10px !important;
}

.message:not(.message--forceColumns) .message-userDetails {
  padding: 0 !important;
}

}
