style.css:
@-moz-document regexp('https://www.baidu.com/s\?((wd)|(ie))=.*') {
  /* 头栏 */
  div#head {
    background-color: #f7f7f7;
  }

  .s_form {
    padding: 0 !important;
  }

  a#result_logo {
    position: absolute;
    top: 0;
    margin-top: 19px !important;
    left: calc((100% - 672px) / 2 - 110px);
  }

  form#form {
    position: absolute;
    margin: 15px auto;
    width: 672px;
    left: calc((100% - 672px) / 2);
  }

  /* Tab栏 */
  div.s_tab {
    padding-left: 0 !important;
    background-color: #f7f7f7 !important;
  }

  div.s_tab_inner {
    text-align: center;
  }

  div.s_tab_inner:first-child {
    margin-left: 45px;
  }

  /* 搜索工具 */
  /* 未打开搜索工具 */
  div.nums {
    margin: 0 auto !important;
    width: 665px;
    text-align: left;
  }

  /* 已打开搜索工具 */
  div.search_tool_conter {
    margin: 0 auto !important;
    width: 665px;
    text-align: left;
  }

  /* 搜索结果 */
  div#wrapper_wrapper {
    background-color: #f7f7f7;
  }

  #container {
    margin: 0 auto;
    text-align: center;
  }

  #content_left {
    margin: 0 auto;
    width: 630px;
    padding: 0 !important;
    float: none;
  }

  div.c-container {
    text-align: left;
    margin: 0 auto;
    margin-right: 0;
    transition-duration: 0.4s;
    width: 570px;
    padding: 25px 30px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
    border-radius: 4px;
  }

  div.c-container:hover {
    box-shadow: 0 14px 25px rgba(0, 0, 0, 0.16);
  }

  div.c-border {
    border-color: #f7f7f7;
    box-shadow: none;
  }

  /* 相关搜索 */
  div#rs {
    margin: 20px auto 0px auto !important;
    background-color: #f7f7f7;
  }

  div#rs > table {
    margin: 0 auto !important;
  }

  /* 页数 */
  div#page {
    text-align: center;
  }

  div.page-inner {
    padding-right: 140px !important;
  }

  /* 页脚 */
  div.foot-inner {
    text-align: center;
  }

  span#help {
    float: none !important;
    padding-right: 140px;
  }
}
