style.css:
@-moz-document domain("allocine.fr") {
/* version 1.1 */

.open-modal .overlay {
  display: none !important;
}

.open-modal body {
  overflow: visible !important;
}

.open-modal body::after {
  display: none !important;
}

.open-modal.no-touch body {
  padding-right: 0 !important;
}
}
