style.css:
@-moz-document domain("vk.com") {
.nim-peer.nim-peer_smaller.online.mobile:after {
  background-color: #373b3e;                                   
}
#page_header_wrap{
  background: #373b3e;   
   box-shadow: 0 0 5px rgba(0,0,0,0.5);
   border-bottom: 0px solid #4872a3;
}
[dir] #page_header_cont .back {
    background: #373b3e;
    border-bottom: none;
}

[dir] .top_nav_btn:hover, [dir] .top_nav_link:hover{
    background-color: #373b3e;
}
.top_nav_link.top_profile_link, .top_nav_btn:hover {
    background: #373b3e;  
}

[dir] .top_nav_link.active, [dir] .top_nav_btn.active{
    background: #282b2f!important;
}

[dir] input.text.ts_input {
    background: #282b2f url(http://u963529r.bget.ru/ic_head_loupe.svg) no-repeat;
    background-position: center left 8px;
    border: none;
    padding-left: 28px;
}
.top_audio_player:hover {
    background-color: #373b3e!important;
}
input.text.ts_input:focus{
    background-color: #282b2f!important;
    border-color: #282b2f!important;
    padding-left: 30px;
}

.top_nav_btn#top_notify_btn .top_nav_btn_icon {
    background-image:url(http://u963529r.bget.ru/ic_head_notify.svg) !important;
}
.top_nav_btn#top_audio .top_nav_btn_icon{
    background-image:url(http://u963529r.bget.ru/ic_head_audio.svg) !important;                                    
}
.ts_wrap .input_back_content, .top_audio_player .top_audio_player_title, .top_audio_play__button:after{
  color: #e0e0e0;             
}
#top_notify_btn.has_notify .top_notify_count {
  border-color: #373b3e;
}
.vkmusic-btn .vkmusic-btn-inner:hover, .vkmusic-btn:hover {
  background-color: rgba(230, 237, 245, 0) !important;
}
.top_audio_play__button, .top_audio_play__button:before, [dir] .top_audio_play__button::before, [dir] .top_audio_play__button {
  background-color: #484b4e;
}
[dir] .top_audio_play__button:hover, [dir] .top_audio_play__button:hover::before{
  background-color: #5c6164;   
}
.top_audio_play__button:after{
  background-image:url(http://u963529r.bget.ru/playbutton.svg) !important;           
}

::-webkit-scrollbar {
    background: #373b3e !important;
}

/* То что ещё не исправлено */

.video_thumb_label, .post_doc_thumb_label_content, .im_sticker_bl_promo, .Button--positive, .videoplayer, .VideoRecomsItem__title, .videoplayer_context_menu ._item:hover, .videoplayer_timeline_preview ._text, .nim-dialog:not(.nim-dialog_deleted):hover .nim-dialog--preview.nim-dialog--preview-attach, [dir] .nim-dialog.nim-dialog_typing .nim-dialog--typing, .page_album_title, .VideoPlaylistItem__counter, .mv_playlist .mv_playlist_header .mv_playlist_header_title, .box_title .back, [dir="ltr"] .nim-dialog .nim-dialog--typer-el::before, [dir="ltr"] .videoplayer_end_suggestions ._item, .videoplayer_end_suggestions ._item_views, [dir] .videoplayer_end_next_block ._title, [dir] .videoplayer_end_next_block ._views, [dir] .videoplayer_end_next_block ._caption, .mv_min_title {
    color: #fff!important;
}
[dir] .videoplayer_slider ._filled{
    background-color: #fff;
}
[dir] #layer_bg, [dir] .VideoRecomsItem:hover {
   background-color: rgba(0,0,0,0.9);
}
[dir] .videoplayer {
    background: black;
}
[dir] #pv_box, [dir] .mv_playlist .mv_playlist_header, [dir] .VideoRecomsItem {
    background: #1e1e1e;
}
[dir] .mv_pl_scrollbar_cont.scrollbar_cont{
    background: #171717;
}
[dir] .VideoRecomsItem.VideoRecomsItem--active {
    background-color: #303031 ;
}
.mem_pseudolink, .im-page-pinned--media {
    color: #8EC5FF;
}

[dir] .ProfileEditorVkconnect__notice {
    background: #373B3E;
}

body{
    color: #fff !important
}
}
