style.css:
@-moz-document domain("studio.youtube.com") {
/* YouTube Studio Dark Theme v8.0 by Lexan*/

/* Dark Scrollbar*/
::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #222 !important;
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: #444 !important;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838 !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
  }

/*Variables*/
:root {
        --none: rbga(0,0,0,0) !important;
        --accent-color: #FF2222 !important;
    	--bg-color: #0F0F0F;
        --bg2-color: #141414 !important;
		--bg3-color: #222;
		--item-color: #333;
		--item-active-color: #777;
        --text-color: #fff;
		--text-darken-color: #ddd;
		--text-darken2-color: #aaa;
        --ytcp-background-0: #222 !important;
        --ytcp-background-10: #141414 !important;
        --ytcp-text-primary: #fff !important;
        --ytcp-text-secondary: #aaa !important;
        --paper-dialog-color: #fff !important;
        --ytcp-white-5: #1a1a1a !important;
        --ytcp-black-3: #aaa !important;
        --ytcp-black-4: #fff !important;
        --ytcp-black-5: #fff !important;
        --yt-blue: #ff2222 !important;
        --ytcp-focus: #ff2222 !important;
        --ytcp-studio-blue: #ff2222 !important;
        --ytcp-studio-light-blue: #ff2222 !important;
        --yt-spec-call-to-action: #ff2222 !important;
        --ytcp-medium-red: #ff2222 !important;
        --ytcp-studio-green: #0b0 !important;
        --ytcp-studio-light-green: #0b0 !important;
        --ytcp-themed-green: #0b0 !important;
        --ytcp-badge-green: #183F1A !important;
        --ytcp-themed-blue: #ff2222 !important;
        --ytcp-badge-blue: var(--item-color) !important;
    --header-background-color: #141414 !important;
    --ytcp-brand-background-solid: #141414 !important;
    --ytcp-line-divider: #222 !important;
    --ytcp-call-to-action: #ff2222 !important;
    --ytcp-call-to-action-raised-background: #ff2222 !important;
    --ytcp-black-secondary: #aaa !important;
    --yt-spec-brand-background-solid: var(--bg3-color) !important;
    --iron-overlay-backdrop-background-color: var(--text-color) !important;
    --ytcp-link-color: #fff!important;
    --ytcp-menu-background: var(--bg2-color) !important;
    --yt-spec-brand-background-primary: var(--bg3-color) !important;
    --yt-spec-text-primary: var(--text-color) !important;
    --yt-spec-text-secondary: var(--text-darken2-color) !important;
}

body {
    background: var(--bg-color);
}

/*Header*/
    .ytcp-home-button img{
        content: url("https://lngnews.ru/files/yt_studio_logo.svg");
        Filter:drop-shadow(0px 0px 4px #0005);
    }

    ytcp-app[enable-page-title] .page-title.ytcp-app /*Page titles*/{
        color: var(--text-color)
    }

    .header.ytcp-content-section /*Headers with tabs*/, ytcp-app[enable-page-title] .page-title-container.ytcp-app /*Title header*/{
        background-color: var(--bg2-color)
    }

    header.ytcp-header /*Header shadow*/{
        box-shadow: rgba(15, 15, 15, 0.5) 0px 0px 0px 1px, rgba(15, 15, 15, 0.5) 0px 4px 4px;
    }

    #create-icon /*create button*/{
        background-color: #444;
        border: none !important;
        border-radius: 5px;
    }

    #create-icon:hover /*create button hover*/{
        background-color: var(--item-active-color);
        transition: .3s;
    }

    #sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child),#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer /*account switcher background*/{
        background-color: var(--bg2-color)
    }

/*Search Box*/
    input.ytcp-omnisearch /*search box*/{
        border: none;
        background-color: var(--bg-color)
    }

    #results.ytcp-omnisearch,ytcp-omnisearch.focused input.ytcp-omnisearch,#row-container.ytcp-video-row, #row-container.ytcp-video-row .floating-column.ytcp-video-row /*background*/{
