    }

/*Sidebar*/
    tp-yt-paper-icon-item.ytcp-navigation-drawer /*Text colour*/{
        color: var(--text-color)
    }   

        ytcp-navigation-drawer[keyboard-focus] .menu-item-link.ytcp-navigation-drawer:focus tp-yt-paper-icon-item.ytcp-navigation-drawer, ytcp-navigation-drawer[keyboard-focus] tp-yt-paper-icon-item.ytcp-navigation-drawer:focus, tp-yt-paper-icon-item.ytcp-navigation-drawer:hover, tp-yt-paper-icon-item.ytcp-navigation-drawer[submenu-open], tp-yt-paper-icon-item.iron-selected.ytcp-navigation-drawer /*Selected sidebar colour*/{
