    }

    ytcp-public-video-thumbnail-with-info.ytcp-post-video-attachment /*latest post thumbnail fix*/{
        background-color: rgba(0,0,0,0);
    }

        .style-scope.tp-yt-iron-icon[d="M4 13h6V7h4L7 0 0 7h4z"],.style-scope.tp-yt-iron-icon[d="M23,12a11,11,0,0,1-3.22,7.78l-1.41-1.41a9,9,0,0,0,0-12.73l1.41-1.41A11,11,0,0,1,23,12ZM5.64,5.64,4.22,4.22a11,11,0,0,0,0,15.56l1.41-1.41a9,9,0,0,1,0-12.73ZM16.95,7.05,15.54,8.46a5,5,0,0,1,0,7.07l1.41,1.41a7,7,0,0,0,0-9.9Zm-9.9,0a7,7,0,0,0,0,9.9l1.41-1.41a5,5,0,0,1,0-7.07Z"],.style-scope.tp-yt-iron-icon[d="M18,10v8H6V6h8l2-2H6A2.15,2.15,0,0,0,4,6V18a2.15,2.15,0,0,0,2,2H18a2.15,2.15,0,0,0,2-2V8Z"] /*header icon colour fix*/{
        fill: var(--text-color)
    }

    #page-title-container{ /*Widescreen fix*/
        max-width: 20000px !important;
    }
        
