    ytcp-video-section-content /*Main background colours*/{
        --header-background-color: var(--bg3-color) !important;
        --row-background-color: var(--bg2-color)
    }

    #row-container.ytcp-video-row.row-highlighted, #row-container.ytcp-video-row.row-highlighted .floating-column.ytcp-video-row /*Row hover colour*/{
