    ytcp-video-visibility-select[first-container-style="selected"] #first-container.ytcp-video-visibility-select, ytcp-video-visibility-select[second-container-style="selected"] #second-container.ytcp-video-visibility-select,#first-container.ytcp-video-visibility-select, #second-container.ytcp-video-visibility-select /*border removal*/{
