style.css:
@-moz-document url-prefix("https://knigavuhe.org")
{
body {
    background: #222 !important;
    color: #999 !important;
}

.header {
    background: #222 !important
}

.header_logo {
    filter: invert(0.25)
}

.header_link2 {
    color: #777
}

.subheader {
    display: none;
}

.page_title,
.book_serie_block_title > a,
.bookkitem_name > a,
.books_right_block_title > a,
.last_comments_comment_book_name > a {
    color: limegreen !important;
}

.user_rating0 {
    color: #999 !important
}

.index_ipbook {
    color: #386ca8;
}

.index_ipbook_chapters {
    color: #999 !important;
}

.pn_show_more {
    background-color: #333;
    border: 1px solid #777;
    color: #999;
}

.bookkitem_date_sep {
    background-color: #333;
}

.bookkitem_date_sep_inner {
    background: #222;
    color: #999;
}

.index_onlyfree_bg {
    background-color: #333;
}

.bookkitem_about {
    color: olive;
}

input[type=text].header_search {
    background: black;
}


a.header_link {
    color: burlywood
}

a.header_link:hover {
    color: darkorange
}

.book_blue_block,
.scroll_fix {
    background-color: #222 !important
}

.book_player {
    width: 365px !important;
}

.book_player_btn.-backward::before,
.book_player_btn.-forward::before,
.book_player_btn.-next::before,
.book_player_btn.-prev::before {
    filter: invert(0.5)
}

.book_player_progress_btn_icon {
    fill: #999;
}

.book_middle_ads {
    display: none
}

.suggested_book,
.header_search_results_section_item_serie_authors {
    color: forestgreen
}

.suggested_book_author_href {
    color: #777
}

.comments_input_wrap {
    background-color: #333
}

.suggested_book:hover {
    color: darkgoldenrod
}

.comments_input {
    background: #999 !important;
}

div.comments_input:empty::before {
    color: black !important;
}

button[type=button].default {
    background: #777;
}

input[type=text].header_search {
    color: lawngreen;
}

.header_search_results {
    background-color: #111;
    color: #777;
}

a.header_search_results_section_item {
    color: forestgreen;
}

.header_search_results_section_item_book_line2 {
    color: #999;
}

.header_search_results_section_item_book_line1 > .__name {
    color: limegreen;
}

.header_search_results_section_item_book_line1 > .__author_label {
    color: darkcyan;
}

.footer_copyright a :first-child {
    filter: invert(0.5);
}

.pn_button {
    background-color: #777;
    border: 1px solid #777;
}
.pn_button_sep,
input.pn_goto_input {
    background: #555;
    border: 1px solid #555;
}

.pn_button.-page_cur{
    background-color: lightslategray;
}
}
