style.css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com"), url-prefix("https://forums.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://forums.roblox.com"), domain("roblox.com") {
/*ROBLOX New Logo Replacement 2.0 by Gustavo- https://web.roblox.com/users/1878060599/profile

/*Should replace the new logo on the website with the old one*/

/*NOTE: Roblox deleted the "R" logo (https://www.roblox.com/images/Logo/roblox_logo.svg) from their site, so I am instead using the full logo, and then scaling it. It is not perfect, but it should do for a high quality svg logo. I don't want to host the "R" logo svg on my own, otherwise I might run into copyright issues. Sorry about this..*/
 .icon-logo {
    /*Main site navbar full logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
}
.icon-logo-r {
    /*Main site navbar small logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: 135px 135px !important;
    background-position-x: 5px !important;
    background-position-y: center !important;
}
.big-logo {
    /*Download page logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    height: 80px !important;
}
.robloxLogo {
    /*Landing page small logo*/
    content: url("http://i.imgur.com/Hj502As.png");
    /*hides the new logo in the worst way possible idk*/
    background-image: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;
    background-size: 167px 167px !important;
    background-position-x: left !important;
    background-position-y: center !important;
    width: 31px;
}
#MainLogoImage {
    /*Landing page big logo*/
    content: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;
    margin-left: 20px !important;
}
#LogoAndSlogan .clearfix::after {
    /*Landing page slogan under the logo*/
    content:"Powering Imagination" !important;
    color: white !important;
    font-size: 35px !important;
    background-color: rgba(0, 0, 0, 0.7) !important;
    padding: 10px 25px !important;
    text-align: center !important;
    margin-top: -20px !important;
    display: inline-block !important;
}
#LogoAndSlogan .clearfix::before {
    /*Slogan trademark thing, needed here so the font can be smaller*/
    content:"â„¢" !important;
    color: white !important;
    position: absolute !important;
    font-size: 15px !important;
    top: 100px !important;
    margin-left: 413px !important;
}
.dark-theme .icon-default-logo-r, .dark-theme .icon-logo-r, .dark-theme .icon-logo-r-95 {
    background-image: url(https://vignette.wikia.nocookie.net/logopedia/images/1/19/ROBLOX_Logo_2015.svg/revision/latest?cb=20170907130237)
}
.light-theme .icon-default-logo-r, .light-theme .icon-logo-r, .light-theme .icon-logo-r-95 {
    background-image: url(https://vignette.wikia.nocookie.net/logopedia/images/1/19/ROBLOX_Logo_2015.svg/revision/latest?cb=20170907130237)
}
}

@-moz-document url-prefix("https://blog.roblox.com"), url-prefix("https://corp.roblox.com/") {
/*ROBLOX Blog*/
 footer .comp-logo a {
    /*Blog footer logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 30px !important;
}
header .site-title a {
    /*Blog header logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 25px !important;
}
}

@-moz-document domain("roblox.com") {
/*Events*/
 .logo-link > img {
    content: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 125% !important;
    margin-top: -15% !important;
}
}

@-moz-document url-prefix("https://www.roblox.com/account/signupredir?"), url-prefix("https://web.roblox.com/account/signupredir?"), url-prefix("https://www.roblox.com/"), url-prefix("https://web.roblox.com/") {
#signup-header .text-logo {
	background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg);
	width: 90%;
	height: 65px;
	max-width: 457px;
}
}

@-moz-document url-prefix("https://web.roblox.com/giftcards"), url-prefix("https://www.roblox.com/giftcards") {
.logo-link > img {
    content: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 125% !important;
    margin-top: -15% !important;
}
}

@-moz-document url-prefix("https://en.help.roblox.com/") {
/*
{{---######---CODE_FOR_SUPPORT---######---}}
*/
#header {
	background-color: #0074bd;
	height: 40px;
}
/* Logo */
.logo img {
	width: 0;
	max-height: 30px;
}
.logo a::after {
	display: inline-block;
	background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg);
	background-size: 100%;
	background-repeat: no-repeat;
	content: 'invisible';
	font-size: 0;
	width: 120px;
	height: 30px;
	position: absolute;
	left: 6px;
	top: 6px;
}
}
