style.css:
@-moz-document url-prefix("https://www.bnr.nl/player/live") {
/* version 20210217 */
.bnr-player header, 
.bnr-player .intro,
.ad,
.ad-rendered,
.bnr-player footer nav img.logo
{ display: none; }

.bnr-player,
.bnr-player header,
.bnr-player #player,
.pf-player-container .pf-default-wrapper,
.bnr-player footer { background-color: #222; }

.pf-player img { opacity: 0.25; }
.bnr-player footer { opacity: 0.67; }

.bnr-player footer { width: 140px; margin: auto; }
.bnr-player footer span.link-text { float: none; margin-right: 0px; }

.bnr-player body>footer { position: relative; }
}
