style.css:
/* i really want this to be global */
/* font & Font size */
html, body, input, select, textarea {
font-size: 14px !important;
font-family: iransansweb;
font-weight: 400;
}


/* Checklist Direction*/
.checklist-item-details .checklist-item-row .checklist-item-text-and-controls {
    padding: 6px 0;
    width: 100%;
    display: inline-flex;
    direction: rtl !important;
    text-align: right;
}

/*rtl checkbox edit*/
.window-main-col .edit textarea.field {
    direction: rtl;
}

.checklist-new-item.editing textarea.checklist-new-item-text, .checklist-new-item.editing textarea.checklist-new-item-text:focus, .checklist-new-item.editing textarea.checklist-new-item-text:hover{
direction: rtl !important;
}

.comment-box-input, .comment-box-input:active, .comment-box-input:focus, .comment-box-input:hover{
direction: rtl !important;
}

.comment-box-input{
direction: rtl !important;
}
