style.css:
@-moz-document domain("flightrising.com") {
/*GENERAL STUFF */

/*dark highlighting and inputs + firefox fixes */
::selection, *::selection {
    filter: none !important;
    background: /*[[selectbg]]*/;
    color: /*[[selectcolor]]*/;
}
/*dark select boxes*/
.common-filter select, .scry-id, .common-dialog .common-dialog-inset {
    background-color: #15191e;
    color: #edf1f9;
    border: #121522;
}
.common-column-content select, select, .dragon-picker-filter-field input, .dragon-picker-filter-field select, .morphology-picker-filter-field input, .morphology-picker-filter-field select {
    background-color: #15191e;
    color: #edf1f9;
    border: 1px solid #121522;
    background-image: url('https://www1.flightrising.com/static/layout/common/select-menu.png');
    background-position: right center;
    background-repeat: no-repeat;
    padding-right: 18px;
    -moz-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}
#coli-dragonlist-controls select, #item-chooser-controls #quantity {
    background-color: #233946;
    border-color: #14252f;
}
#item-chooser-controls #quantity {
    width: 100%;
}
/* dark empty checkboxes, bright blue with black checkmarks when selected */
.common-checkbox {
    background: #15191e;
    border: 1px solid #546d86;
}
.common-checkbox:hover {
    background: #263646;
    border: 1px solid #6a859f;
}
.common-checkbox[data-selected="1"] {
    border-color:#4df0f0;
    background-color:#4df0f0;
    color: #000;
}
.common-checkbox[data-selected="1"]::after {
    filter: brightness(0);
}
/* lower brightness of separators */
img[src*="/images/layout/graydot.gif"], img[src*="/static/layout/graydot.gif"], img.divider {
    opacity: 0.2;
}
/*pagination fixing*/
.common-pagination-pages {
    display: flex;
    justify-content: center;
    align-items: center;
}
.common-pagination .common-pagination-page {
    margin: 0 2px;
}
/*fix issue w/ buttons being over alerts*/
.statdiv span #alerticon, #trade-chat span, #messicon, #frenicon, span.pending-avatar-msg-count {
	z-index: 2;
	position: relative;
}

/*fix dev tracker*/
#dev-tracker-content .dev-tracker-search-result-message-fade {
    font-weight: bold;
    text-transform: uppercase;
    color: #00f1ff;
    background: linear-gradient(to bottom, rgba(243,236,228,0) 0%,rgb(19, 23, 27) 80%);
}

/*fix speech bubble issue caused by main style*/
.common-npc-dialog[data-tail="left"]::before {
    border-color: transparent #060d11 transparent transparent !important;
}
.common-npc-dialog[data-tail="left"]::after {
	border-color: transparent #060d11 transparent transparent !important;
}

/*wrap long usernames in forum posts*/
a.post-author-username, .post-author-username, .editor-author-username {
    word-wrap: break-word;
    max-width: 90%;
    line-height: 100%;
}

/* makes choosing dragon vista background dark when no vista is selected */
.choose-vista-preview {
    background-color: #22262a !important;
}

/*more obvious disabled buttons*/
#nonewoffer, button:disabled, *[type=button]:disabled, #notrade.mb_button {
    opacity: 0.45;
}

/*lucky streak label fix*/
#lucky-streak {
    color: #000
}

/* full width gathering buttons */
form[action*="main.php?p=gather&"] .beigebutton.thingbutton:only-child, form[action*="main.php?p=gather&"] .greybutton.stopbutton:only-child {
    width: 95%;
}
form[action*="main.php?p=gather&"] .greybutton.stopbutton {
    opacity: 0.5;
    background-color: #4d677d !important;
    -webkit-box-shadow: none;
}
/* gathering pages - fix huge empty space for extra bonus items from site events*/
div[style*="position:absolute; left:125px; top:90px; z-index:0;"] + div[style="position:relative; z-index:1; font-size:12px; min-height: 194px;"] {
    min-height: unset !important;
    }

/*fixing btns*/
.beigebutton.thingbutton {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
}

/* make the orange alerts blue on old pages */
span#alert div[style*="background-image:url(images/layout/alert_box_bg.png)"] {
    background-image: url(https://i.imgur.com/fkzzcPi.png) !important;
}

/*dark scry share widget*/
.scry-image .scry-image-rear {
    background: none;
    position: relative;
    z-index: 2;
}
.scry-image .scry-buttons {
    z-index: 3;
}
.scry-image .scry-image-rear::before {    
    content: "";
    background-image: url('https://www1.flightrising.com//static/layout/scryer/bg_scrywidget.png');
    background-size: cover;
    position: absolute;
    top: 0px;
    right: 0px;
    bottom: 0px;
    left: 0px;
    opacity: 0.75;
    z-index: -1;
    filter: invert(100%) sepia(100%) hue-rotate(190deg) saturate(390%) brightness(70%);
    pointer-events: none;
}
.scry-image .scry-name, .forum-map-widget-frame .forum-map-widget-button-text, .settings-hub-radio-control[data-selected="0"] {
    color: #78c6f0 !important;
}
/*dark map poi share widget*/
.forum-map-widget-frame {
    background: linear-gradient(to bottom, #161f31 0%, #212b40 100%);
    border: 1px solid #5b6682;
}
.forum-map-widget-frame .forum-map-widget-button {
    background: linear-gradient(to bottom, #192031 0%, #192031 100%);
    border: 1px solid #5d6476;
}


