@-moz-document url-prefix("https://www1.flightrising.com/market") {
/* darken new marketplace */
.flash_sale_tab_icon img {
    filter: drop-shadow(1px 1px 3px rgba(255, 174, 125, .569)) drop-shadow(-1px -1px 3px rgba(255, 186, 54, .459));
}
.market-item-result-name, .market-item-result-name-hot {
    top: 13px;
    color: #70d2ff;
}
