    background: #191e24;
    border-color: #546d86;
}
.market-item-result.market-flash-result {
    background:  linear-gradient(to bottom, #43211b 0%, #563926 50%, #695124 100%);
    border-color: #b36f27;
    position: relative;
}
.market-item-result.market-flash-result::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    background: url("https://www1.flightrising.com/static/layout/market/flash_sale_background.png");
    width: 100%;
    height: 100%;
    opacity: 0.8;
    filter: invert(100%) hue-rotate(180deg);
    border-radius: 8px;
}
.market-item-price-container, .gem-pricing-container {
    background: #384558;
    color: #d5e0e6;
    border-color: #111;
}
