/*↑-------暗色主题下一些设置------↑*/



/*@页面背景*/
body {
	background-color: var(--wolai-bg) !important;
	overflow: hidden;
}
[style*="rgba(255, 255, 255, 0.7)"]{
	color: rgba(255, 255, 255, 0.9) !important;
}
#root {
	z-index: 1;
	background-position: center top;
	background-size: cover;
}
#root:after {
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	z-index: -1;
	background-image: var(--wolaistyleplus-page-background-image);
	background-position: center top;
	background-size: cover;
	background-color: rgba(255, 255, 255, .15);
	background-attachment: fixed;
	-webkit-filter: var(--wolaistyleplus-page-background-image-blur);
	filter: var(--wolaistyleplus-page-background-image-blur) var(--wolaistyleplus-page-background-image-brightness);
	margin: -50px !important;
	transform: scale(1.10);
}
/*页面磨砂背景*/
._1N86P {
	/*顶部题图背景*/
	background-color: transparent;
}
._3pA8I {
	/*空白页面默认模板区背景*/
	background-color: transparent;
}

._3rVip ._10sAo {
	/*页面编辑区*/
	padding: 10px 25px 40px 25px;
	max-width: 100%;
	min-width: 760px!important;
	border-radius: 12px;
	background: var(--wolaistyleplus-page-editer-bgcolor);
	box-shadow: 0 8px 32px 0 rgba( 31, 38, 135, 0.37);
	backdrop-filter: blur(15px);
	-webkit-backdrop-filter: blur( 15px);
	border: 1px solid rgba( 255, 255, 255, 0.2);
}




/*-------@左侧边栏------*/
._2q7II {
	/*background-color: var(--wolaistyleplus-sidebar-bgcolor) !important;*/
	background-color: transparent !important;
}

._108s8,
._1CSYc,
#wolai-sidebar {
	background-color: var(--wolaistyleplus-sidebar-bgcolor) !important;

}

.tpGAY:hover {
	background-color: var(--wolai-hover-bg) !important;

}
.treeBeanSelected {
	background-color: var(--wolaistyleplus-left-Selected-bgcolor) !important;
	/*白色模式下左侧边栏被选中的页标题背景色 */
}
._14df9 ._3pAxv {
	color: var(--wolai-svg-CF5659);
	/*左侧目录中鼠标hover的文字色*/
}
._3o1Po:not(._2AVeS):hover {
	background-color: var(--wolai-sidebar-tree-node-selected-bg);
	/*左侧目录中鼠标hover的背景色*/
}
._3o1Po ._4T5BJ path {
	color: var(--wolai-svg-CF5659);
	fill: var(--wolai-svg-CF5659);
	/*左侧目录中>符号颜色*/
}
i[style="font-size: 18px; color: rgb(207, 86, 89);"] {
	color: var(--wolai-svg-CF5659) !important;
	/*图标是fa格式时，选中的目录图标颜色*/
}
