}
/*↑-------模板按钮------↑*/
/*------------------------------------------------------------------------------------------------------------------*/
/*-------代码片段------*/
._2TL9K {
	outline: none;
	text-align: left;
	font-size: 16px;
	min-width: 0px;
	height: 100%;
	width: 100%;
	font-family: JetBrainsMono-Regular, SFMono-Regular, Consolas, 'Liberation Mono', Menlo, monospace;
	font-family: 'Consolas'!important;
	/*代码片段字体和大小*/
	color: var(--wolai-code-node-color);
	background-color:transparent;
}
._2ms-i {
	border-radius: 6px;
	/*代码区背景边框圆角半径*/
	border: 2px solid var(--wolai-border-color);
	text-align: left;
	background: var(--wolai-code-node-bg) !important;
	/*代码区背景颜色，颜色见24和46行*/
	min-width: 0px;
	min-height: 42px;
	/*代码区最小高度*/
	width: 100%;
	padding: 15px;
	/*代码区边框度*/
	display: -webkit-box;
	display: flex;
	-webkit-box-orient: horizontal;
	-webkit-box-direction: normal;
	flex-direction: row;
	overflow-x: auto;
}
/*↑-------代码片段------↑*/
/*------------------------------------------------------------------------------------------------------------------*/
/*-------附件------*/
._1dtSv {
	/*文件下载块样式*/
	background-color: rgba(177, 177, 177, 0.1);
	-webkit-box-flex: 1;
	flex: 1;
	border-radius: 6px;
	overflow: hidden;
	position: relative;
	border: 2px solid rgba(200, 200, 200, 0.24) !important;
