style.css:
@-moz-document domain(app.sortd.com) {
    #appContainer {
        filter: invert(0.9);
    }
    .primCol {
        filter: invert(1);
    }
    .titleColumnAddTask span {
        filter: invert(1);
    }
    .primary {
        filter: invert(1) !important; 
    }
    input, textarea, select {   
        filter: invert(1) !important; 
    }
    .topPanel div:nth-child(4) {
        filter: invert(1) !important; 
    }
    sd-search-box input {
        filter: invert(0) !important;     
    }
    .button.gmail {
        filter: invert(1) !important; 
    }
    img {
        filter: invert(1);
    }
    div.color1,div.color2,div.color3,div.color4,div.color5,div.color6,div.color7,div.color8,div.color9,div.color10,div.color11,div.color12,div.color13,div.color14,div.color15,div.color16,div.color17,div.color18,div.color19,div.color20 {
        filter: invert(1);
    }
    .taskAction {
        filter: invert(1);
    }
    .taskAction .upcoming {
        filter: invert(1);
    }

    .actions {
        filter: invert(1);
    }
    button {
        filter: invert(1);
    }
    .actions.padFlex button {
        filter: invert(0) !important;
    }
    .link {
        filter: invert(1);
    }
    .badge.categoryList_CustomColor3 {
        filter: invert(1) !important;
    }
    .header {
        border-bottom-color: #fb4c2f;
    }
    .link.selected {
        border-bottom-color: #00bcd4 !important;
    }
    .round-button {
       filter: invert(1) !important; 
    }
    .mailItem.currentItem {
        border-left-color: #fb4c2f !important;
    }
    .mailItem.selectedItem {
        filter: invert(1) !important;
    }
    svg {
        filter: invert(1) !important; 
    }
    .dropDown > .text {
        filter: invert(1) !important; 
    }
    .tabLink.selected {
        filter: invert(1) !important; 
    }
    menuitem.active {
        filter: invert(1) !important; 
    }
    .boardCard {
        filter: invert(1) !important; 
    }
    a.active {
        filter: invert(1) !important; 
    }
    div.containerHeader.sortable > h3 > div > sd-drop-down > div > div.text > span {
        color: white;
    }
    sd-drop-down em {
        filter: invert(1); 
    }
}
