style.css:
@-moz-document url-prefix("https://dota2.ru/") {
body{
    font-family: Arial;
    background-color: #0b0b0d;
}

#project-dota2{
    padding-top: 0;
    background-color: #121215;
}

.subtitle-global{
    border-radius: 3px 3px 0px 0px;
    background-color: #2b2c32;
    display: block;
    min-height: 40px !important;
    max-height: 40px;
    padding: 8px 0px 4px;
    text-align: center;
    border: none;
}

.forum-customization, .news-customization {
    right: 7px;
    top: 7px;
    background: #28282d;
}

.btn-global{
    padding: 0;
    margin: 0 6px;
    font-size: 16px;
    background-color: #00000000;
}

.btn-global--active{
    color: #00c0ff;
    background-color: #00000000;
}

.component-block{
    background-color: #1b1c20
}

.component-block__list{
    padding-bottom: 0;
}

.component-block__link{
    padding: 0 12px !important;
}


/* NEWS */

.index__main{
    background-color: #1b1c20;
    margin-right: 12px;
    margin-left: 0;
    order: 0;
}

#news-categories{
    justify-content: left !important;
    max-height: 40px !important;
    padding: 4px !important;
    border-radius: 3px 3px 0px 0px !important;
    background-color: #2b2c32 !important;
    margin-bottom: 12px !important;
}

#news-categories-blocks{
    padding: 0px 12px !important; 
}

@media (max-width: 1023px){
    #news-categories-blocks{
        padding: 0px !important;
    }
}

.index__news-item{
    margin-bottom: 12px;
}

.index__news-name{
    margin-top: -4px;
}


/* FORUM PANEL */

.index__left-bar{
    order: 1;
    margin-right: 12px;
}

.forum__list .forum__item{
    background: #1b1c20;
}

.forum__block{
    border: none;
    padding: 7px 0 5px;
    flex-direction: row !important;
}

.forum__list .forum__item:nth-of-type(2n){
    background: #222328;
}

.forum .forum__item .component-block__img{
    display: none;
}

.forum .forum__item:nth-child(1), .forum .forum__item:nth-child(2), .forum .forum__item:nth-child(3), .forum .forum__item:nth-child(4){
    margin-left: -2px;
    border-left: solid 2px #00beff;
}

.forum__item .text-clip{
    width: calc(100% - 50px);
}

.forum__item .component-text-grey-11 {
    white-space: nowrap;
}

.index__right-bar > div, .index__left-bar > div{
    border: none;
    margin-top: 0px;
    margin-bottom: 12px;
}

/* MEMES */

.index__right-bar{
    order: 2;
}

.memes__user{
    padding: 8px 12px 7px;
}

/* STREAMS */

.streams .streams__item:nth-of-type(2n) a{
    background: #222328;
}

.streams__item-component{
    min-height: 48px;
    padding: 4px 0;
    border: none !important;
}

.streams__item .component-block__img{
    border-radius: 18px !important;
    width: 36px !important;
}

/* TOURNAMENTS */

.tournament .tournament__item:nth-of-type(2n) a{
    background: #222328;
}

.tournament__item .component-block__block{
    border: none;
    padding: 8px 0;
}

/* VIDEOS */

.video .video__item:nth-of-type(2n) a{
    background: #222328;
}

.video__item .component-block__block{
    border: none;
    font-size: 12px !important;
    padding: 0;
}

.video__img{
    width: auto;
    height: 56px;
    margin-top: 4px;
}

.video__item .streams__text{
    padding-top: 4px;
    font-size: 11px !important;
}

/* PLAYERS */

.players .players__item:nth-of-type(2n) a{
    background: #222328;
}

.players__item .component-block__block{
    border: none;
    font-size: 12px !important;
    padding: 8px;
}

.players__item .component-block__img{
    height: 36px !important;
}
}

@-moz-document url-prefix("https://dota2.ru/forum/") {
.global-main__wrap > div{
    margin-bottom: 12px;
}

.bg-main-block{
    padding: 0;
    margin-bottom: 12px;
    background: none;
}

.forum-page__section{
    padding: 0px;
}

.forum-page__btn{
    background-color: #1b1b1f !Important;
}

.forum-page__title-block{
    border-radius: 3px 3px 0 0;
    background-color: #2b2c32;
    padding: 6px 6px 6px 12px;
}


/* CHAT */

#chatFull{
    padding: 0;
}

.forum-page__chat-messager{
    padding: 4px;
    resize: vertical;
    background-color: #1b1c20;
}

.forum-page__chat-message-wrap{
    background-color: #252528;
}

.forum-page__chat-message-name{
    font-weight: bold;
    font-size: 14px;
}

.forum-page__chat-message-name:hover{
    text-decoration: underline;
}

.scroll-down{
    bottom: 100px;
}

/* THREADS */

.forum-page__list{
    padding: 0;
}

.forum-page__item:before, .forum-page__item:after{
    background: none !important;
}

.forum-page__item-list{
    margin: -15px 0 0px 60px;
    padding-bottom: 5px;
}

.forum-page__item-list li{
    padding: 0px 8px;
}

.forum-page__item-title-block{
    padding: 8px 12px;
    border-top: solid 2px #121215;
}
}

@-moz-document url-prefix("https://dota2.ru/forum/forums/") {
.forum-page__item{
    background-color: #1b1c20 !important;
}

.forum-section__list .forum-section__item:not(:first-child){
    background-color: #1b1c20;
}

.forum-section__list .forum-section__item{
    padding: 4px 12px;
    border-top: solid 2px #121215;
}

.sticky{
    background-color: #222328 !important;
}
}

@-moz-document url-prefix("https://dota2.ru/forum/") {
/* GOLOSOVANIE!!!11!1 */

#poll-form{
    background: #10101000 !important;
    padding: 0;
}

.poll-block__header-title{
    font-size: 24px;
    margin-bottom: 5px;
}

.poll-block__body-item{
    border: solid 1px #121215;
    width: 500px;
    height: 40px;
}

button.poll-block__body-item:last-child{
    background-color: #28282d;
}

button.poll-block__body-item:last-child:hover{
    background-color: #28282d;
}

.poll-block__body-item--count{
    color: #ababb2;
}

.poll-block__body-item--name{
    color: #ccc;
}

.poll-block__body-item--bar{
    background-color: #2e73a0;
}

.forum-theme__item-dots--options .forum-theme__item-dots-block{
    background-color: #28282d;
}

/* FORUM MESSAGE */

.forum-theme__block{
    border-radius: 6px 6px 0 0;
    margin-bottom: 20px;
}

/* AVATAR BLOCK */

.forum-theme__item-left{
    background-color: #1b1c20;
    padding: 6px;
    margin-right: 12px;
    border-right: solid 2px #242424;
    border-bottom: solid 2px #242424;
    width: auto;
    height: min-content;
    border-radius: 6px;
}

.forum-theme__item-name{
    max-width: 150px;
    text-align: center;
}

.forum-theme__item-status{
    max-width: 150px;
    display: flex;
    justify-content: center;
    align-items: normal;
}

.forum-theme__item-status span{
    margin-top: 6px;
}

.forum-theme__item-avatar{
    width: 150px;
    margin: 0;
}

.forum-theme__item-avatar > img{
    max-width: 150px;
    padding: 5px;
}

.forum-theme__item-info-desk p{
    color: #888888;
    padding: 0 6px;
    height: 14px;
}

.forum-theme__item-info{
    white-space: nowrap;
}
.forum-theme__item-block-mess {
 border-radius: 6px 6px 0px 0px;
}

.forum-theme__item-block-achiv {
    margin-top: 0px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    height: min-content;
}

.forum-theme__item-block-achiv div img{
    width: 38px;
}

.forum-theme__item-block-achiv div img[src='/img/dota_plus.png']{
    margin-top: -3px;
    width: 26px;
}

.forum-theme__item-right > div{
    border-right: solid 2px #242424;
    padding: 10px;
    background-color: #1b1c20;
}

/* MESSAGE BLOCK */

/* time */
.forum-theme__item-right .forum-theme__item{
    order: 1;
    padding: 15px 10px 10px;
    height: 30px;
}

.forum-theme__item-right .forum-theme__item time{
    color: #666;
}

/* bottom panel */

.forum-theme__item-bottom{
    order: 1;
    border-bottom: solid 2px #242424;
    border-radius: 0 0 6px 6px;
    padding: 6px 10px 10px !important;
    margin-top: 0;
    border-top: solid 2px #333333;
    align-items: normal;
}

.forum-theme__item-bottom div, .forum-theme__item-bottom a, .forum-theme__item-bottom span{
    background-color: #303035;
}

.forum-theme__item-bottom img{
    height: 28px;
    margin-right: 2px;
}

.forum-theme__item-bottom li > span{
    padding: 2px 4px;
    margin-top: 6px;
}

#rate-btn{
    height: 32px;
    margin-top: 6px;
    padding: 0;
}

.forum-theme__item-bottom .forum-theme__item-btn:hover{
    transition: 0s;
}

.forum-theme__item-bottom span span{
    margin-top: 2px;
    font-size: 16px;
}

.forum-theme__item-like-item{
    margin-right: 4px;
}

.forum-theme__item-like-link:hover {
    background: #28282d;
}

.forum-theme__item-btn{
    margin-top: 6px;
    height: 32px;
}

.forum-theme__item-dots{
    background-color: transparent !important;
    margin-right: 0px;
    margin-top: 9px;
    padding: 0px;
    display: flex;
    justify-content: right !important;
    align-items: center;
    width: auto;
}

.forum-theme__item-dots span{
    display: none;
}

.forum-theme__item-dots .forum-theme__item-dots-block{
    background-color: transparent;
    bottom: 0;
    transform: none;
    position: relative;
    display: inline-flex;
    flex-direction: row;
    padding: 0;
    margin: 4px 0 0 0;
}

.forum-theme__item-dots .forum-theme__item-dots-block a{
    height: 32px;
    padding: 0 7px;
    margin-right: 5px;
}


/* Otvet */

#quick-reply{
    width: calc(100% - 176px);
    margin-left: auto;
}

@media(max-width: 900px){
    #quick-reply{
        width: 100%;
    }
}

#forumPost_ifr{
    padding: 10px 10px;
}

/* TOX */

form:not(.global-content__edit-post) .tox-tbtn {
    margin: 0 2px !important;
    width: 26px !important;
}

.tox-split-button *{
    margin: 0 !important;
    padding: 0 !Important;
}

.tox-split-button:hover{
    box-shadow: 0 0 0 transparent !important;
}

form:not(.global-content__edit-post) .tox-tbtn {
    width: 26px;
}

.tox .tox-split-button .tox-split-button__chevron{
    width: 16px !important;
    margin-left: -5px !important;
    margin-right: 5px;
}

.tox-tinymce{
    padding: 0 !important
}

.tox-tbtn:hover{
    background-color: transparent !important;
}

.tox-tbtn svg{
    margin: 0px -2px;
    cursor: pointer;
    filter: brightness(120%)
}

.tox-tbtn:hover svg{
    filter: brightness(250%)
}

.tox-tbtn[aria-disabled='true']{
    display: none;
}

.tox-toolbar{
    border-bottom: solid 2px #333333 !important;
}

form:not(.global-content__edit-post) .tox-tbtn.tox-tbtn--bespoke {
    width: 65px !important;
} 

/* PING */

.bbCodeQuote{
    margin-top: 5px;
    padding: 0;
    border: 0;
    background-color: #00000000;
}

.bbCodeQuote .attribution{
    border-radius: 6px 6px 0 0;
    border-bottom: solid 2px #28282d;
    margin: 0;
    padding: 6px 12px 4px;
    background-color: #080808;
}

.bbCodeQuote .quote{
    border-radius: 0 0 6px 6px;
    background-color: #121212 !important;
    padding: 8px 12px 6px;
}

.quoteContainer .forum-theme__item-block-remess-btn{
    margin: 0 !important;
    background: #080808 !important;
    border-radius: 0 0 6px 6px;
}

.forum-theme__item-block-mess .attribution abbr {
    font-weight: 700;
}


/* SPOILER */

.spoiler-btn{
    padding: 2px 14px 0;
    color: #bbb;
    font-weight: 500;
    background-color: #080808 !important; 
}

.spoiler-content{
    background: #202227 !important;
}
}
