style.css:
@-moz-document domain("wolai.com") {

/*-------wolai云笔记文字批注------*/
div [style*="background-color:rgba(237, 233, 235, 0.09)"] {
	/*暗色白灰背景色按钮 三角符号线 */
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: rgba(200, 200, 200, 0.2) !important;
	border-radius: 20px;
	padding: 3px 8px 3px 8px;
	-webkit-text-emphasis: filled triangle;
	text-emphasis: filled triangle;
	-webkit-text-emphasis-position: under right;
	text-emphasis-position: under right;
	text-emphasis-color: red;
	-webkit-text-emphasis-color: red;
	letter-spacing: 0px !important;
}
/*暗色白灰按钮*/
div [style*="background-color:rgba(237, 233, 235, 0.5)"] {
	/*白色主题白灰背景色按钮 三角符号线  */
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: rgba(200, 200, 200, 0.2) !important;
	border-radius: 20px;
	padding: 3px 8px 3px 8px;
	-webkit-text-emphasis: filled triangle;
	text-emphasis: filled triangle;
	-webkit-text-emphasis-position: under right;
	text-emphasis-position: under right;
	text-emphasis-color: red;
	-webkit-text-emphasis-color: red;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(247, 214, 183, 0.5)"] {
	/*圆点符号  对应幼杏色背景按钮*/
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: rgba(200, 200, 200, 0.2) !important;
	border-radius: 20px;
	padding: 3px 8px 3px 8px;
	-webkit-text-emphasis: filled circle;
	text-emphasis: filled circle;
	-webkit-text-emphasis-position: under right;
	text-emphasis-position: under right;
	text-emphasis-color: red;
	-webkit-text-emphasis-color: red;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(217, 211, 215, 0.2)"],
[style*="background-color:rgba(217, 211, 215, 0.5)"] {
	/*带波浪线的文字样式  对应暗银背景按钮*/
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: rgba(200, 200, 200, 0.2) !important;
	border-radius: 20px;
	padding: 2px 8px 5px 8px;
	text-decoration: underline wavy #fd0000;
	margin: 0px;
}
div [style*="background-color:rgba(169, 222, 249, 0.5)"] {
	/*天藍文字背景色按钮 批注 */
	background-color: #89d3f9 !important;
	color: #000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(169, 222, 249, 0.5)"]:after {
	content: " 刪 ";
	/*带删字批注  天藍文字背景色按钮*/
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
	color: #53c0f9;
	background-color: var(--wolai-editor-bg) !important;
}
div [style*="background-color:rgba(205, 243, 220, 0.5)"] {
	/*浅绿文字背景色按钮 批注 */
	background-color: #bbc61c !important;
	color: #000000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(205, 243, 220, 0.5)"]:after {
	content: " 改 ";
	/*改字批注 浅绿文字背景色按钮 */
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
	color: #aab50b;
	background-color: var(--wolai-editor-bg) !important;
}
div [style*="background-color:rgba(255, 193, 153, 0.5)"] {
	/*橘色文字背景色按钮 批注 */
	background-color: #f4a35f !important;
	color: #202020 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(255, 193, 153, 0.5)"]:after {
	content: " 注 ";
	/*注释批註  橘色文字背景色按钮*/
	color: #f4a35f;
	background-color: var(--wolai-editor-bg) !important;
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
}
div [style*="background-color:rgba(252, 246, 189, 0.5)"] {
	/*淡黄文字背景色按钮 批注 */
	background-color: #f6cc33 !important;
	color: rgb(0, 0, 0) !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(252, 246, 189, 0.5)"]:after {
	content: " 亮 ";
	/*高亮批註  淡黄文字背景色按钮*/
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
	color: #F6CC33;
	background-color: var(--wolai-editor-bg) !important;
}
div [style*="background-color:rgba(239, 218, 251, 0.5)"] {
	background-color: #cb99fc !important;
	/*带評的批註  对应紫背景按钮*/
	color: #000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(239, 218, 251, 0.5)"]:after {
	content: " 评 ";
	/*带評的批註  对应紫色背景按钮*/
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
	color: #cb99fc !important;
	background-color: var(--wolai-editor-bg) !important;
}
div [style*="background-color:rgba(253, 198, 200, 0.5)"] {
	/*疑问批注  对应将红背景按钮*/
	background-color: #f78a85 !important;
	color: #000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(253, 198, 200, 0.5)"]:after {
	content: " 问 ";
	/*疑问批注  对应将红背景按钮*/
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
	color: #f78a85;
	background-color: var(--wolai-editor-bg) !important;
}
div [style*="background-color:rgba(189, 201, 255, 0.5)"] {
	/*雾蓝文字背景色按钮 批注 */
	background-color: #97f3d9 !important;
	color: #000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(189, 201, 255, 0.5)"]:after {
	content: " 补 ";
	/*带补字批注  雾藍文字背景色按钮*/
	color: #97f3d9;
	background-color: var(--wolai-editor-bg) !important;
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
}
div [style*="background-color:rgba(234, 202, 220, 0.5)"] {
	/*熏粉文字背景色按钮 批注 */
	background-color: #ffcfd1 !important;
	color: #000000 !important;
	border-radius: 20px;
	padding: 1px 5px 3px 12px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
}
div [style*="background-color:rgba(234, 202, 220, 0.5)"]:after {
	content: " 析 ";
	/*赏析分析批注  雾藍文字背景色按钮*/
	color: #ffcfd1;
	background-color: var(--wolai-editor-bg) !important;
	width: 20px;
	font-size: 12px;
	font-weight: 800;
	border-radius: 20px;
	margin: 0px -2px 4px 8px;
	padding: 1px;
}
/*以上是文字批注的样式*/
/*以下样式时将按钮下的文字改成对应的内容*/
.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"] + div:before {
	/*暗色主题白灰按钮*/
	content: '三角';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.09);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"] + div:before {
	/*白色主题白灰按钮*/
	content: '三角';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(237, 233, 235, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] + div:before {
	/*熏粉按钮*/
	content: '赏析';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(234, 202, 220, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"] + div:before {
	/*暗色模式下暗银按钮*/
	content: '波浪';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.2);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"] + div:before {
	/*白色模式下暗银按钮*/
	content: '波浪';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(217, 211, 215, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"] + div:before {
	/*幼杏按钮*/
	content: '圆点';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(247, 214, 183, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] + div:before {
	/*橘色按钮*/
	content: '注释';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(255, 193, 153, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] + div:before {
	/*淡黄按钮*/
	content: '高亮';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(252, 246, 189, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] + div:before {
	/*浅绿按钮*/
	content: '修改';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(205, 243, 220, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] + div:before {
	/*天蓝按钮*/
	content: '删除';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(169, 222, 249, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] + div:before {
	/*轻紫按钮*/
	content: '评语';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(239, 218, 251, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"] + div:before {
	/*将红按钮*/
	content: '疑问';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(253, 198, 200, 0.5);"] + div {
	font-size: 0;
}
.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"] + div:before {
	/*雾蓝按钮文字*/
	content: '补充';
	visibility: visible;
	display: block;
	font-size: 12px;
}
.text-action-bar-color div[style="background: rgba(189, 201, 255, 0.5);"] + div {
	/*雾蓝按钮*/
	font-size: 0;
}
/*↑-------文字批注------↑*/
}
