style.css:
/* 
version beta-0.1.0
*/

@-moz-document url("https://www.bilibili.com/"), url("https://message.bilibili.com/pages/nav/index_new_pc_sync"), url("https://t.bilibili.com/pages/nav/index_new"), url("https://www.bilibili.com/page-proxy/game-nav.html"), url("https://live.bilibili.com/blackboard/dropdown-menu.html"), url("https://manga.bilibili.com/eden/bilibili-nav-panel.html") {
:root {
    --color-bg-1: #1e2228;
    --color-bg-2: #2d333b;
    --color-bg-3: #22272e;
    --color-bg-4: #444c56;
    --color-bg-overlay: #323941;
    --color-header-bg: #2d333b;
    --color-header-search-bg: #22272e;
    --color-header-search-border: #373e47;
    --color-border-1: #444c56;
    --color-border-2: #373e47;
    --color-text-1: #adbac7;
    --color-text-2: #768390;
    --color-text-3: #768390;
    --color-btn-bg: #373e47;
    --color-btn-border: #444c56;
    --color-btn-text: #adbac7;
    --color-btn-hover-bg: #444c56;
    --color-btn-hover-border: #768390;
    --color-input-bg: #22272e;
    --color-hover-1: #444c56;
    --color-selection-bg: #1890ff;
    --color-selection-text: #ffffff;
    --color-scrollbar-thumb-bg: #686868;
    --color-scrollbar-track-bg: #424242;
    --color-hover-scrollbar-thumb-bg: #7b7b7b;
    --color-active-scrollbar-thumb-bg: #a1a1a1;
}
/* 滚动条 */
 ::-webkit-scrollbar {
    width: auto;
    height: auto;
}
::-webkit-scrollbar-thumb {
    border: 2px solid var(--color-scrollbar-track-bg);
    background-color: var(--color-scrollbar-thumb-bg);
}
::-webkit-scrollbar-thumb:hover {
    background-color: var(--color-hover-scrollbar-thumb-bg);
}
::-webkit-scrollbar-thumb:active {
    background-color: var(--color-active-scrollbar-thumb-bg);
}
::-webkit-scrollbar-track {
    background-color: var(--color-scrollbar-track-bg);
}
/* 文本选中 */
 ::selection {
    background-color: var(--color-selection-bg);
    color: var(--color-selection-text);
}
}

@-moz-document url("https://www.bilibili.com/") {

html, body, #app {

    background-color: var(--color-bg-1);

}

.international-footer {

    background-color: var(--color-bg-2);

}

body {

    color: var(--color-text-1);

}

a, .storey-title .no-link {

    color: var(--color-text-1);

}

/* 
登录提示窗
*/

.login-tip[class] {

    background-color: var(--color-bg-overlay);

}

.content .lt-text[class] {

    color: var(--color-text-1);

}

/* 
未登录时，“登录”悬浮窗
*/

/* 小三角 */

.unlogin-popover[class]:before,

/* 主体 */

.unlogin-popover[class] {

    background-color: var(--color-bg-overlay);

}

.unlogin-popover-avatar .title[class], .unlogin-popover-avatar .register-tip[class] {

    color: var(--color-text-1);

}

.unlogin-popover-avatar .register-tip[class] {

}

/* 联系客服 */

.contact-help {

    background: var(--color-bg-overlay);

    border-color: var(--color-border-1);

    box-shadow: none;

    color: var(--color-text-1);

}

/* 
banner顶部导航栏
*/

/* 未登录时，下滑页面后，悬浮在顶部的导航栏 */

.international-header.fixed-top .mini-header {

    background-color: var(--color-header-bg);

}

.international-header.fixed-top .mini-type .nav-link .nav-link-ul .nav-link-item .link, .international-header.fixed-top .mini-type .nav-user-center .user-con .item .name {

    color: var(--color-text-1);

}

/* 搜索框 */

.international-header .mini-type .nav-search #nav_searchform, .van-popover .mini-type .nav-search #nav_searchform, .bili-header-m .nav-search #nav_searchform, .international-header .nav-search #nav_searchform {

    border-color: var(--color-header-search-border);

    background-color: var(--color-header-search-bg);

}

/* 搜索建议面板 */

.bilibili-search-suggest,

/* 搜索记录面板 */

.bilibili-search-history {

    border-color: var(--color-border-1);

    background-color: var(--color-bg-overlay);

}

/* 搜索框-聚焦 */

.bili-header-m .nav-search .nav-search-keyword:focus, .international-header .nav-search .nav-search-keyword:focus,

/* 搜索建议面板-建议项 */

.bilibili-search-suggest .suggest-item a,

/* 搜索记录面板-记录项 */

.bili-header-m .nav-search .nav-search-keyword, .international-header .nav-search .nav-search-keyword, .bilibili-search-history .history-item, .bilibili-search-history .history-item a, .bilibili-search-history .history-item a:hover {

    color: var(--color-text-1);

}

/* 搜索建议面板-建议项-hover */

.bilibili-search-suggest .suggest-item.focus, .bilibili-search-suggest .suggest-item:hover,

/* 搜索记录面板-记录项-hover */

.bilibili-search-history .history-item.focus, .bilibili-search-history .history-item:hover {

    background-color: var(--color-hover-1);

}

/* 搜索记录面板-记录项-删除 */

.bilibili-search-history .cancel-icon {

    color: var(--color-text-2);

}

.bili-header-m .nav-search .nav-search-btn, .international-header .nav-search .nav-search-btn {

    background-color: var(--color-btn-bg);

}

.bili-header-m .nav-search .nav-search-submit, .international-header .nav-search .nav-search-submit {

    color: var(--color-btn-text);

}

/* 用户-悬浮菜单 */

.van-popper-avatar .level-content .level-info .grade {

    color: var(--color-text-1);

}

.van-popper-avatar .level-content .level-info .progress {

    color: var(--color-text-2);

}

.van-popper-avatar .vp-container .coins, .van-popper-avatar .vp-container .counts, .van-popper-avatar .vp-container .links, .lang-change {

    border-bottom-color: var(--color-border-1);

}

.van-popper-avatar .counts .count-item .item-key, .van-popper-avatar .links .link-icon, .lang-change .lang-icon, .van-popper-avatar .logout .link-icon {

    color: var(--color-text-2);

}

.van-popper-avatar .counts .count-item .item-value, .van-popper-avatar .vp-container .links .link-title, .lang-change .lang-title, .van-popper-avatar .vp-container .logout, .van-popper-avatar .level-content .level-intro, .lang-change .lang-intro, .lang-change .lang-icon-more, .van-popper-avatar .vp-container .logout span:hover {

    color: var(--color-text-1);

}

.van-popper-avatar .level-content .level-intro, .lang-change .lang-intro {

    background-color: var(--color-bg-overlay);

}

.van-popper-avatar .vp-container .links .link-item:hover, .lang-change .lang-item:hover, .lang-change .lang-intro-item:hover, .van-popper-avatar .vp-container .logout span:hover {

    background-color: var(--color-hover-1);

}

/* 大会员-悬浮窗 */

.vip-m .bubble-traditional .recommand .title, .vip-m .bubble-traditional .recommand .bubble-col .item .recommand-link {

    color: var(--color-text-1);

}

/* 悬浮窗小三角 */

.nav-item .i-frame::before {

    background-color: var(--color-bg-overlay);

}

/* 
收藏-悬浮窗
*/

.van-popper-favorite .tabs-panel .tab-item--normal, .favorite-video-panel .header-video-card .video-info .line-2 {

    color: var(--color-text-1);

}

.van-popper-favorite .vp-container .tabs-panel {

    border-right-color: var(--color-border-1);

}

.van-popper-favorite .tabs-panel .tab-item--normal:hover, .favorite-video-list .header-video-card:hover {

    background-color: var(--color-hover-1);

}

.van-popper-favorite .tabs-panel .tab-item .num, .favorite-video-list .header-video-card .video-info .info, .van-popper-favorite .tabs-panel .tab-item__all {

    color: var(--color-text-2);

}

.van-popper-favorite .tabs-panel .tab-item--active .num {

    color: #fff;

}

.van-popper-favorite .tab-item__all {

}

.van-popper-favorite .play-view-all .play-all {

    background-color: var(--color-btn-bg);

    border-top-color: var(--color-btn-border);

}

.van-popper-favorite .play-view-all .play-all.view {

    color: var(--color-text-1);

}

.van-popper-favorite .play-view-all .play-all:hover {

    background-color: var(--color-hover-1);

}

/* 
历史-悬浮窗
*/

.van-popper-history .vp-container .tab-header {

    border-bottom-color: var(--color-border-1);

}

.van-popover, .van-popper-history .panel .date-title, .van-popper-history .header-video-card .video-info .info, .van-popper-history .header-video-card .video-info .bilifont {

    color: var(--color-text-2);

}

.van-popper-history .header-video-card .video-info .line-2 {

    color: var(--color-text-1);

}

.van-popper-history .header-video-card:hover {

    background-color: var(--color-hover-1);

}

.van-popper-history .panel .view-all {

    color: var(--color-btn-text);

    background-color: var(--color-btn-bg);

}

.van-popper-history .panel .view-all:hover {

    color: var(--color-btn-text);

    background-color: var(--color-btn-hover-bg);

}

/* 
投稿-悬浮窗
*/

.van-popper-upload .upload-item .bilifont {

    color: var(--color-text-2);

}

.van-popper-upload .vp-container .upload-item:hover {

    color: var(--color-text-1);

    background-color: var(--color-hover-1);

}

/* 
banner底部导航栏
*/

/* 字体颜色 */

.international-header a, .van-popover a {

    color: var(--color-text-1);

}

/* 折叠菜单 */

.van-popover {

    background-color: var(--color-bg-overlay);

    border-color: var(--color-border-2);

}

.van-popper[x-placement^=bottom] .popper__arrow:after {

    border-bottom-color: var(--color-border-2);

}

.van-popper[x-placement^=top] .popper__arrow:after {

    border-top-color: var(--color-border-2);

}

/* 左侧四个按钮 */

.page-tab .con li {

    border-color: transparent;

}

/* 分隔线 */

.tab-line-itnl {

    border-left-color: var(--color-border-1);

}

/* “换一换”、“更多”按钮 */

.rcmd-box-wrap .change-btn, .exchange-btn .btn, .rank-header .more {

    border-color: var(--color-btn-border);

    background-color: var(--color-btn-bg);

    color: var(--color-brn-text);

}

.rcmd-box-wrap .change-btn:hover, .exchange-btn .btn:hover, .rank-header .more:hover {

    background-color: var(--color-btn-bg);

    border-color: var(--color-btn-hover-border);

}

/* 
右侧悬浮导航栏
*/

/* 主体 */

.elevator .list-box,

/* 返回顶部按钮 */

.elevator .list-box .item.back-top {

    background: var(--color-bg-2);

    border-color: var(--color-border-2);

}

.elevator .list-box .item {

    background: transparent;

}

/* 排序按钮外层 */

.elevator .list-box .item.sort {

    border-top-color: var(--color-border-1);

}

/* 排序按钮 */

/* 返回顶部按钮 */

.elevator .list-box .item .bilifont {

    color: var(--color-text-1);

}

/* 
“推广”栏的up主昵称小字
*/

.video-card-common .ex-up,

/* “专栏”up主昵称小字 */

.article-card .r-con .up,

/* 其他up主昵称小字 */

.video-card-common .up {

    color: var(--color-text-2);

}

/* 
栏目标题后的小字描述
*/

.storey-title .text-info, .storey-title .text-info a {

    color: var(--color-text-2);

}

/* 
“观看列表”按钮
*/

.bypb-window .online {

    background-color: var(--color-bg-2);

    border-color: var(--color-border-2);

}

/* 
“正在直播”栏
*/

/* 直播标题 */

.live-card .up .txt .desc {

    color: var(--color-text-3);

}

/* 直播tag */

.live-card .up .txt .tag {

    color: var(--color-text-2);

}

/* 右侧栏-直播标题 */

.live-rank .live-rank-item .txt p.p2 {

    color: var(--color-text-2);

}

/* 右侧栏-直播人气统计 */

.live-rank .live-rank-item .count {

    color: var(--color-text-3);

}

/* 右侧栏-序号-非高亮 */

.live-rank .live-rank-item .number,

/* “动画”栏-右侧栏-序号-非高亮 */

.rank-wrap .number,

/* “漫画”栏-右侧栏-序号-非高亮 */

.manga-rank-item .rank-number,

/* 其他-右侧栏-序号-非高亮 */

.pgc-rank-wrap .number {

    background-color: var(--color-bg-2);

    color: var(--color-text-2);

}

/* 
“动画”栏
*/

/* 右侧栏-“综合得分”小字 */

.rank-wrap .preview .txt span {

    color: var(--color-text-2);

}

/* 右侧栏-悬浮弹窗 */

.popover-video-card {

    background-color: var(--color-bg-overlay);

}

.popover-video-card .content .info .f-title {

    color: var(--color-text-1);

}

.popover-video-card .content .info .subtitle {

    color: var(--color-text-2);

}

.popover-video-card .count {

    border-top-color: var(--color-border-1);

}

.popover-video-card .count ul li {

    color: var(--color-text-3);

}

/* 
“番剧”栏目
*/

/* “新番时间表”按钮 */

.time-line .tl-link {

    background-color: var(--color-btn-bg);

}

/* 主体栏-剧集更新小字 */

.time-line-card .txt .update a,

/* 右侧栏-剧集更新小字 */

.pgc-rank-wrap .txt .update {

    color: var(--color-text-2);

}

/* 
“漫画”栏目
*/

/* “下载app”小字 */

.manga-panel .app-download-link,

/* 漫画标题底部tag */

.manga-panel .manga-list-box .manga-card .manga-tag,

/* 右侧栏-漫画类型小字 */

.manga-rank-item .preview .preview-desc .style,

/* 右侧栏-漫画剧集更新小字 */

.manga-rank-item .preview .preview-desc .update, .manga-rank-item .other-link .update {

    color: var(--color-text-2);

}

/* 漫画标题 */

.manga-panel .manga-list-box .manga-card .manga-title {

    color: var(--color-text-1);

}

/* 
“课堂”栏目
*/

/* 更新信息的小字 */

.video-card-common .update_info {

    color: var(--color-text-2);

}

/* 
“专栏”栏目
*/

/* 统计信息的小字 */

.article-card .r-con .count {

    color: var(--color-text-3);

}

/* 
底部
*/

.international-footer .link-box .link-item .bt, .international-footer .partner, .international-footer .partner a {

    color: var(--color-text-2);

}

.international-footer .link-box .link-item ul a, .international-footer .link-box .link-item.link-c p {

    color: var(--color-text-1);

}

.international-footer .link-box .link-item {

    border-right-color: var(--color-border-1);

}
}

@-moz-document url("https://www.bilibili.com/page-proxy/game-nav.html") {

/* 
首页中，顶部导航栏的“游戏中心”悬浮窗
*/

.box {

    box-shadow: none;

    background-color: var(--color-bg-overlay);

}

.all a, .brief span {

    color: var(--color-text-1);

}

.brief a:hover, .all a:hover {

    background-color: var(--color-hover-1);

}
}

@-moz-document url("https://live.bilibili.com/blackboard/dropdown-menu.html") {

/* 
首页中，顶部导航栏的“直播”悬浮窗
*/

#app-ctnr .live-box {

    background-color: var(--color-bg-overlay);

}

.hot-live .room-list .list-item .uname {

    color: var(--color-text-1);

}

.hot-live .room-list {

    border-right-color: var(--color-border-1);

}

.hot-act .act-list .list-item:hover {

    background-color: var(--color-hover-1);

}
}

@-moz-document url("https://manga.bilibili.com/eden/bilibili-nav-panel.html") {

/* 
首页中，顶部导航栏的“漫画”悬浮窗
*/

.app-layout[class] {

    background-color: var(--color-bg-overlay);

    box-shadow: none;

}

.recommendation-list .list-item .item-title[class], .popularity-list .list-title[class], .popularity-list .item-list .list-item[class] {

    color: var(--color-text-1);

}

.recommendation-list .list-item[class]:before, .popularity-list .item-list .list-item[class]:hover {

    background-color: var(--color-hover-1);

}

.app-layout .divider[class] {

    background-color: var(--color-border-1);

}
}

@-moz-document url("https://message.bilibili.com/pages/nav/index_new_pc_sync") {

/* 
首页中，顶部导航栏的“消息”悬浮窗
*/

.im-root {

    color: var(--color-text-1);

}

.im-list-box {

    background-color: var(--color-bg-overlay);

}

.im-list:hover {

    color: var(--color-text-1) !important;

    background-color: var(--color-hover-1) !important;

}
}

@-moz-document url("https://t.bilibili.com/pages/nav/index_new") {

/* 
首页中，顶部导航栏的“动态”悬浮窗
*/

#app .out-container {

    background-color: var(--color-bg-overlay);

}

.out-container .tab-bar {

    color: var(--color-text-2);

    border-bottom-color: var(--color-border-1);

}

.container .no-more-tip {

    color: var(--color-text-2);

}

.tip-box+.split-line::before {

    border-top-color: var(--color-border-1);

}

.tip-box+.split-line {

    color: var(--color-text-2);

}

.tip-box+.split-line .history-tip {

    background-color: transparent;

}

.list-item .name-line, .list-item .publish-time {

    color: var(--color-text-2);

}

.list-item .content {

    color: var(--color-text-1);

}

.container .list-item:hover {

    background-color: var(--color-hover-1);

}

.more-tab .more-btn {

    color: var(--color-btn-text);

    background-color: var(--color-btn-bg);

}

.more-tab .more-btn:hover {

    background-color: var(--color-btn-hover-bg);

}
}
