style.css:
@-moz-document url-prefix("https://v2.horsereality.com/user/26499") /* RUNES */
{
/* BACKGROUND */
body.background.north-america, body.background.south-america, body.background.europe, body.background.australia, body.background.asia, body.background.africa
{
	background-image: url("https://i.imgur.com/fTpmPe7.png");
  background-size: cover;
  background-position: 0%;
}
/* V2 MENU */
.header-menu /*BASE COLOUR */
{
  background: #47543B;
}
  .header-menu nav .main-menu .user-nav .dropdown-menu
{
  background: #47543B;
}
.side-nav
{
  background: #F5F7F3;
}
.side-nav ul a 
{
  color: #71835D;
}
.progress-bar
{
  background-color: #8A9A5B;
}
.footer 
{
  background: #505D42;
}
.header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item:hover /* LINK COLOUR */
{
  color: #CAD2B2;
}
.header-menu nav .main-menu ul li a:not(.btn):hover
{  
  color: #CAD2B2;
}
.header-menu nav .main-menu .user-nav>a:hover
{
  color: #CAD2B2;
}
.show-profile .navbar .nav-link:hover
{
  background-color: #869970;
  color: #fff;
}
.navbar-dark:hover .navbar-nav:hover .nav-link:hover 
{
    color: white;
    background: #869970;
}
.side-nav ul .special a:hover
{
  color: #47543B;
}

/* PROFILE */
.show-profile .navbar /* PROFILE TAB */
{
  background-color: #505d42!important;
  border-top: 2px solid #505d42;
  border-bottom: 2px solid #505d42;
}
.show-profile .profile-account
{
  background-color: #F5F7F3;
}
.show-profile .navbar-avatar .navbar-avatar-container
{
  border: 2px solid #505D42;
}
.component.frame .wrapper /* HORSES TAB */
{
  background: #505D42;
}
.component.table .table thead th
{
  background: #869970;
}
td.horse-name-cell a
{
  color: #869970;
}
.component.table .table tbody tr:nth-of-type(odd) 
{
  background-color: #F5F7F3;
}
.sales-table-row .odd-row /* SALES TAB */
{
  background-color: #F5F7F3;
}
.sales .horse-name {
  font-weight: 600;
  color: #869970;
}
.btn.btn-cta, .btn.btn-primary
{
  background-color: #8A9A5B;
  border-color: #8A9A5B;
}
.topic-title a /* FORUM TAB */
{
  color: #869970;
}
a {
  color: #869970;
}
}

