style.css:
@-moz-document domain("vk.com") {
body {
    --nav-bar-color: #3a4259;
    --nav-bar-icon-color: #656a87;
    --nav-bar-border-color: #464655;
    --top-search-color: #59678e;
    
    --accent: #5f9ec4;
    --selection_off_icon: #667;
    --background_content: #222230;
    --background_light: #3b3a4c;
    --text_primary: #cfcfcf;
    --text_secondary: #aaa;
    --separator_common: #7a7a95;
    --separator_alternate: #686873;
    --text_link: #79b2e4;
    --background_page: #222230;
    
    --button-secondary-background-color: var(--light_blue_300);
    --button-secondary-background-color-hover: var(--light_blue_400);
    --button-secondary-background-color-active: var(--light_blue_500);

}
body {
    color: var(--text_primary) !important;
    background: var(--background_page) !important;
}
#page_header_cont {
    background-color: #3a4259 !important;
    border-bottom: 1px solid #464655 !important;
}
#ts_input {
    background-color: #4d5467 !important;
    border: 1px solid #49506392;
}
.top_notify_header {
    color: var(--text_primary) !important;
}
#top_notify_wrap {
    border: 1px solid var(--separator_common);
}
.top_notify_show_all {
    border-top: 1px solid var(--separator_common);
}
.feedback_row_clickable:not(.feedback_row_touched):hover {
    background-color: none !important;
    border-bottom: 1px solid var(--steel_gray_80) !important;
}
.top_notify_cont .feedback_row:not(.dld).hover,
.top_notify_cont .feedback_row:not(.dld):hover,
.top_notify_show_all:hover {
    background-color: #edeef01a !important;
}
.mention_tt_actions {
    background-color: var(--background_content);
    border-top: 1px solid var(--background_content);
}
.feedback_header .author,
.feedback_header .mem_link,
.feedback_header .group_link,
.ts_contact_name{
    color: var(--text_link);
}
.ts_cont_wrap {
    background: var(--background_light) !important;
    border: 1px solid var(--separator_common) !important;
}
a.ts_contact.active, a.ts_search_link.active {
    background-color: #edeef01a !important;
}
.top_notify_cont,
.top_notify_cont .feedback_header {
    color: var(--text_primary);
}
.LeftMenu__icon {
    color: #79b2e4 !important;
}
.ui_rmenu_slider._ui_rmenu_slider {
    background-color: #5f9ec4 !important;
    width: 2.5px !important;
}
.ui_rmenu_item_sel, [dir="ltr"] .ui_rmenu_item_sel:hover {
    border-left: 2px solid #5f9ec4 !important;
}
.im-page_classic.im-page .im-page--header {
    border-left: solid 1px var(--background_page) !important;
    border-right: solid 1px var(--background_page) !important;
}
#im_dialogs {
    background: none !important;
}
.nim-dialog, body.night .nim-dialog:hover {
    border-top: 1px solid #7a7a95 !important;
}
.nim-dialog_unread-out:not(.nim-dialog_selected) .nim-dialog--cw {
    background: rgba(147, 173, 200, 0.2) !important;
}
.nim-dialog_unread-out .nim-dialog--inner-text {
    color: #dcdcdc !important;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted):hover + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted):hover + .nim-dialog {
    border-top: 1px solid #7a7a95 !important;
}
.online::after {
    border-color: #363639 !important;
}
.online.mobile::after {
    background-color: #363639 !important;
}
.flat_button.secondary, .button_gray button {
    color: #3d3d3d !important;
}
.flat_button.secondary:hover, .button_gray button:hover {
    color: #2a2a2a !important;
}
.flat_button.secondary:active, .button_gray button:active {
    color: #999 !important;
}

.ui_search_new.ui_search_field_empty .ui_search_button_search {
    background-color: var(--background_light) !important;
    border: 1px solid var(--separator_common) !important;
    height: 35.5px !important;
    border-left: none !important;
}
.ui_search_new.ui_search_field_empty .ui_search_button_search:hover {
    background-color: #535672 !important;
}
input.ui_search_field {
    background-color: var(--background_light) !important;
    color: var(--text_primary) !important;
}
.ui_search_new .ui_search_input_inner {
    border: 1px solid var(--separator_common) !important;
}

#profile_editor input.dark[disabled] {
    background: var(--background_light) !important;
    border-color: var(--separator_common) !important;
}
.pedit_separator {
    border-color: var(--separator_common) !important;
}
.result_list {
    border: 1px solid var(--separator_common) !important;
}
.result_list ul li.active {
    background: var(--background_light) !important;
}
.result_list ul li {
    background: var(--background_content) !important;
}
.result_list ul li, .result_list ul li.active {
    color: var(--text_primary) !important;
}
.selector_focused table.selector_table {
    border-color: var(--separator_common) !important;
}
.selector_container table.selector_table {
    border: 1px solid var(--separator_common) !important;
}
.selector_container {
    background: var(--background_light) !important;
    color: var(--text_primary) !important;
}
.selector_input {
    color: var(--text_primary) !important;
}
input.text, input.text ~ .placeholder .ph_input,
input.search, input.search ~ .placeholder .ph_input, input.big_text, input.big_text ~ .placeholder .ph_input,
input.dark, input.dark ~ .placeholder .ph_input, .fakeinput, .fakeinput ~ .placeholder .ph_input,
div[contenteditable="true"], div[contenteditable="true"] ~ .placeholder .ph_input,
textarea, textarea ~ .placeholder .ph_input {
    border: 1px solid var(--separator_common) !important;
}
[dir] div.submit_post_field, [dir] div.submit_post_field[contenteditable="true"], [dir] div.submit_post_field ~ .placeholder .ph_input, [dir] div.submit_post_field[contenteditable="true"] ~ .placeholder .ph_input {
    border: none !important;   
}
[dir] .SitpostingEntrypoint::before {
    background-color: var(--steel_gray_500) !important;
}

.ProfileEditorVkconnect__notice {
    background: var(--background_light) !important;
}
.trg-b-banner .trg-b-text-c {
    filter: invert(0.76);
}
.ads_ad_box.redesign .ads_ad_description {
    color: var(--text_primary) !important;
}

[dir] .audio_layer_container .audio_page_player_wrap {
    background-color: var(--background_light) !important;
    border-bottom: 1px solid var(--separator_common) !important;
}
.page_block_h2 {
    background-color: var(--background_light) !important;
}
.CatalogSection, [dir] .CatalogBlock--divided {
    background-color: var(--background_light) !important;
}
.PlaceholderSmall__title {
    color: var(--text_primary) !important;
}
.PlaceholderSmall__text {
    color: var(--text_secondary) !important;
}
.audio_page__shuffle_all .audio_page__shuffle_all_button {
    color: var(--text_primary) !important;
}
.audio_page__shuffle_all .audio_page__shuffle_all_button::before {
    filter: brightness(1.35) saturate(0.7);
}
[dir] .audio_page_player2 .audio_page_player_track_slider.slider.slider_size_1 .slider_slide, [dir] .audio_page_player2 .audio_page_player_volume_slider.slider.slider_size_1 .slider_slide {
    background-color: #575765 !important;
}
.audio_page_layout .audio_friends_list {
    background-color: var(--background_light) !important;
}
.audio_friend_name, .audio_friend_name_now {
    color: var(--text_primary) !important;
}
.audio_layer_container .audio_friend:hover {
    background-color: #edeef01a !important;
}
[dir="ltr"] .CatalogSection__leftColumn {
    border-right: 1px solid var(--separator_common) !important;
}
[dir="rtl"] .CatalogSection__leftColumn {
    border-left: 1px solid var(--separator_common) !important;
}
.CatalogBlock__layout--in_block_separator {
    background-color: var(--separator_common) !important;
}
.eltt {
    border: 1px solid var(--separator_common) !important;
}
[dir] .ui_scroll_default_theme > .ui_scroll_bar_container > .ui_scroll_bar_outer > .ui_scroll_bar_inner {
    background: var(--steel_gray_200) !important;
}

.audio_page_player2 .audio_page_player_title_song {
    color: var(--text_primary) !important;
}
.CatalogBlock--divided {
    background: var(--background_light) !important;
}
.CatalogBlock--divided::before, .audio_promo {
    box-shadow: 0 1px 0 0 var(--separator_common),0 0 0 1px var(--separator_alternate) !important;
}
.audio_page_layout .audio_search_wrapper {
    background: var(--background_light) !important;
}
.audio_block_small_item--artist .title_link, .audio_block_small_item--curator .title_link,
.audio_page_layout .audio_item__title, .audio_page_layout .audio_item__title > a,
.BannerItem__title {
    color: var(--text_primary) !important;
}
.BannerItem {
    background-color: #575765 !important;
}
.audio_row .audio_row__chart_info_position {
    color: var(--text_primary) !important;
}

[dir] .flat_button.ui_load_more_btn {
    background-color: #575765 !important;
    border-top: 1px solid var(--separator_common) !important;
    color: var(--text_primary) !important;
}

[dir] .fc_tab_head {
    background-color: #3a4259 !important;
    border: 1px solid #464655 !important;
}
[dir] div.fc_clist_filter_wrap {
    border-top: 1px solid var(--separator_alternate) !important;
    background: var(--background_light) !important;
}
[dir] #fc_contacts {
    background: var(--background_content) !important;
}
.fc_contact_name {
    color: var(--text_link) !important;
}
.fc_contact_over {
    background-color: #edeef01a !important;
}
[dir="ltr"] .chats_sp.fc_clist_search_icon {
    margin: 10px 6px 0 1px !important;
}
[dir] #fc_clist_filter, [dir] #fc_clist_filter ~ .placeholder .ph_input {
    padding: 4px 7px 3px !important;
    border: 1px solid #49506392 !important;
}
[dir] #fc_ctabs_cont {
    border: 1px solid var(--separator_alternate) !important;
}
[dir="ltr"] .fc_tab_pointer::before {
    border-left-color: var(--separator_alternate) !important;
}
[dir="ltr"] .fc_tab_pointer::after {
    border-left-color: var(--background_light) !important;
}
[dir] .fc_tab_pointer::after {
    margin: -4.5px 0 !important;
}
[dir] .chat_onl_inner {
    background-color: #4d5467 !important;
}
[dir] .chat_tab_wrap:hover {
    background-color: #434558 !important;
}
}
