style.css:
@-moz-document domain("linkedin.com") {
:root
{
    --main-bg-color: hsl(213, 10%, 17%);
    --main-fg-color: #bbb;
    --light-bg-color: hsl(213, 10%, 23%);
    --light-fg-color: #ccc;
    --lighter-bg-color: hsl(213, 10%, 29%);
    --lighter-fg-color: #ddd;
    --highlight-bg-color: hsl(213, 10%, 35%);
    --highlight-fg-color: #eee;
    --dim-fg-color: #999;
    --disable-bg: hsl(0, 10%, 15%);
    --disable-fg: hsl(0, 10%, 50%);
    --border-color: #888;
    --primary-color: #1a76d2;
}


:root, html, body
{
    background: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
}

textarea, input[type="date"],
input[type="datetime"],
input[type="datetime-local"],
input[type="email"],
input[type="month"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
input[type="time"],
input[type="url"],
input[type="week"]
{
    border-color: var(--border-color);
}

textarea:hover, input[type="date"]:hover,
input[type="datetime"]:hover,
input[type="datetime-local"]:hover,
input[type="email"]:hover,
input[type="month"]:hover,
input[type="number"]:hover,
input[type="password"]:hover,
input[type="search"]:hover,
input[type="tel"]:hover,
input[type="text"]:hover,
input[type="time"]:hover,
input[type="url"]:hover,
input[type="week"]:hover
{
    border-color: var(--main-fg-color);
}

input::placeholder,
input[type="date"]::placeholder,
input[type="datetime-local"]::placeholder,
input[type="datetime"]::placeholder,
input[type="email"]::placeholder,
input[type="month"]::placeholder,
input[type="number"]::placeholder,
input[type="password"]::placeholder,
input[type="search"]::placeholder,
input[type="tel"]::placeholder,
input[type="text"]::placeholder,
input[type="time"]::placeholder,
input[type="url"]::placeholder,
input[type="week"]::placeholder,
select::placeholder,
textarea::placeholder
{
    color: var(--disable-fg);
}

hr
{
    border-color: var(--border-color);
}

.btn__tertiary--medium:hover
{
    background: var(--lighter-bg-color) !important;
}

/* Login Page */

#app__container
{
    background: var(--main-bg-color);
}

#app__container .footer__base__wrapper p.copyright,
.footer__base .footer__base__nav-list a,
.language-selector__button
{
    color: var(--main-fg-color);
}

li-icon[type="linkedin-logo"] svg
{
    filter: hue-rotate(180deg) invert(0.8);
}

.language-selector .language-selector__dropdown
{
    background: var(--main-bg-color);
    box-shadow: 0 0 0 1px var(--border-color);
}

.card-layout
{
    background: var(--light-bg-color);
}

.header__content .header__content__heading,
.header__content .header__content__subheading
{
    color: var(--main-fg-color);
}

.form__input--floating
{
    background: var(--light-bg-color);
}

.form__input--floating > input
{
    color: var(--main-fg-color);
}

.form__input--floating > label,
.form__input--floating > input:focus + label,
.form__input--floating > input:autofill + label,
.form__input--floating > input:valid + label,
.form__input--floating > input:active + label
{
    color: var(--dim-fg-color);
}

/* Nav */

.global-nav
{
    background: var(--main-bg-color) !important;
}

input.search-global-typeahead__input
{
    background: var(--light-bg-color);
    color: var(--light-fg-color);
}

.search-global-typeahead__search-icon
{
    color: var(--light-fg-color);
}

.search-global-typeahead--focused input.search-global-typeahead__input,
.search-typeahead-v2 .basic-typeahead__triggered-content
{
    background-color: var(--light-bg-color);
}

.search-global-typeahead--focused input.search-global-typeahead__input.always-show-placeholder::placeholder
{
    color: var(--light-fg-color) !important;
}

.global-nav__primary-link-text
{
    color: var(--main-fg-color) !important;
}

.global-nav__primary-link,
.global-nav__primary-link:visited,
a.global-nav__primary-link,
a.global-nav__primary-link:visited
{
    color: var(--main-fg-color) !important;
}

.global-nav__primary-link:active,
.global-nav__primary-link:active .global-nav__primary-link-text,
.global-nav__primary-link:hover,
.global-nav__primary-link:hover .global-nav__primary-link-text,
a.global-nav__primary-link:active,
a.global-nav__primary-link:active .global-nav__primary-link-text,
a.global-nav__primary-link:hover,
a.global-nav__primary-link:hover .global-nav__primary-link-text
{
    color: var(--lighter-fg-color) !important;
}

.global-nav__primary-link--active,
.global-nav__primary-link--active .global-nav__primary-link-text,
.global-nav__primary-link--active:visited,
a.global-nav__primary-link--active,
a.global-nav__primary-link--active .global-nav__primary-link-text,
a.global-nav__primary-link--active:visited
{
    color: var(--highlight-fg-color) !important;
}

.global-nav__primary-link::after,
a.global-nav__primary-link::after
{
    border-color: var(--highlight-fg-color) !important;
}

.artdeco-dropdown__content
{
