style.css:
@-moz-document domain("courses.edx.org") {
    .status,
    .problem-progress,
    .correct,
    .incorrect,
    .submission-feedback,
    .vert-due-date {
        visibility: collapse !important;
    }
    
    .mymodal-wrap {
        visibility: collapse !important;
    }
    
    input[type="text"] {
        visibility: visible !important;
        border: 1px solid lightgray !important;
        color: white !important;
        background-color: white !important;
    }
    
    input[type="radio"],
    input[type="checkbox"],
    button[type="button"] {
        display: none !important;
    }
    
    .field-label {
        border: 1px solid lightgray !important;
    }
}
