style.css:
@-moz-document domain("youtube.com") {
/*hide likes numbers*/
.ytd-toggle-button-renderer #text,
#sentiment {
	display: none;
}

/*hide comments*/
#comments {
	display: none
}


/*hide subject under description*/
.ytd-video-secondary-info-renderer.style-scope.sticky {
	display: none;
}


/*hide recs*/
#related {
	display: none;
}
ytd-rich-section-renderer.ytd-rich-grid-renderer.style-scope {
	display: none;
}

/*hide chat*/
#chat {
	display: none;
}

/*watched overlay*/
.resume-playback-background,
ytd-thumbnail-overlay-resume-playback-renderer {
	background: rgba(0, 0, 0, 0.8) !important;
	position: absolute;
	left: 0;
	top: 0;
	display: block !important;
	height: 100%;
	width: 100%;
	pointer-events: none;
	text-align: center;
}

.resume-playback-background::before,
ytd-thumbnail-overlay-resume-playback-renderer::before {
	content: "WATCHED";
	color: white;
	font-weight: bold;
	box-sizing: border-box;
	width: auto;
	height: 100%;
	display: flex;
	pointer-events: none;
	font-size: 200%;
	flex-direction: row;
	align-items: center;
	justify-content: center;
}


/*remove watch later and add to queue*/
div#hover-overlays {
	display: none !important;
}


/*hide other youtube content*/
ytd-guide-section-renderer.ytd-guide-renderer.style-scope:nth-of-type(4) {
	display: none;
}


/*hide tags*/
#header > .ytd-rich-grid-renderer.style-scope {
	display: none;
}


/*hide end cards*/
div[class*="video-player"]:not(:hover) div[class^="ytp-ce"],
div[class*="ytp-autohide"] div[class^="ytp-ce"] {
	display: none !important;
}
}
