	/*spinner*/
	.ytp-spinner-container 
{
    animation: spinner 1s linear infinite;
    opacity: 1;
	border-radius: 50%;    
}
	.ytp-spinner-rotator {
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-animation: spinner!important;
    animation: spinner!important;
}
	
	.ytp-spinner-container div
{
	position: absolute;
	transform: scale(1.1);
	border-radius: 50%;
	background: linear-gradient(#3ea6ff , #ff00e0, #ffeb3b);
	animation: spinner2 1s alternate infinite
}
	.ytp-spinner-container div:nth-child(1) 
{
	filter: blur(5px);
	box-shadow: 0px 0px 9px 1px /*[[yadtheme]]*/
	
}
	.ytp-spinner-container div:nth-child(2) 
{
	filter: blur(50px)	
}
	.ytp-spinner-container div:nth-child(3) 
{
	filter: blur(10px);
	box-shadow: 0px 0px 9px 1px /*[[yadtheme]]*/
}
	.ytp-spinner-container:after,
	.ytp-spinner-left
{
	content: "";
	position: absolute;
	top: 1px;
	left: 1px;
	right: 1px;
	bottom: 1px;
	background: #000;
	border-radius: 50%;
	
	
}
	@keyframes spinner2
{
	0%{transform: scale(1.3)}
	100%{transform: scale(1)}
}
	@keyframes spinner
{
	0%{transform: rotate(0deg); filter: hue-rotate(-50deg)}
	100%{transform: rotate(360deg)}
}
	.ytp-spinner-left .ytp-spinner-circle 
