style.css:
@-moz-document url-prefix("https://app.clickup.com") {
    html, body {
        font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        font-size: 14px !important;
        line-height: normal !important;
        background-color: #e8ecee;
    }
    .cu-data-view-group-label__main-title {
        font-size: 16px;
        line-height: 20px;
    }
    .cu-wip-limit__widget{
        visibility: hidden !important;
    }
    .cu-dashboard-board__column {
        width: 280px !important;
        min-height: 200px !important;
    }
    .cu-panel-board__clickable-name {
        font-size: 14px;
        line-height: 22px;
    }
    section.cu-panel-board {
        border-radius: 8px;
        box-shadow: none;
        transition-duration: 200ms;
        transition-property: background,border-color,box-shadow;
    }
    .cu-data-view-item__icon {
        display: none;
    }
    .cu-panel-board__header-hierarchy {
        margin: 4px 4px 4px 0px;
        font-size: 12px;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
        display: none;
    }
    .cu-dashboard-board-card__features {
    }

    .cu-panel-board__header-box {
        background: none;
    }

    .cu-panel-board__header-options {
        display: none;
    }

    .cu-data-view-group-label__main-title {
        font-weight: bold;
        letter-spacing: 1px;
    }

    .cu-panel-board.cu-panel-board_caption:hover {
        background: none;
        box-shadow: none;
    }

    .cu-panel-board__clickable-name {
        hyphens: none;
    }

    .cu-panel-board__header-container {
        padding: 12px 10px 2px;
        min-height: 100px;
    }
}
