style.css:
@-moz-document url-prefix("https://web.prisga.id/") {
body{
background-color: black;
color: white
}

.card{
color: white;
background-color: black;
border: 2px solid #262626;
border-radius: 10px;
box-shadow: none
}

div{
color: white !important;
}

.page-title-content{
background-color:black
}

span{
color: #606770 !important;
}

.post-item-time a{
color:#95A1AF !important
}

.repost-list .post-item-time{
color:#95A1AF !important
}

.item-comments-count{
color:#60676C !important
}

.post-text{
color: #F2F2F2 !important
}

.item-selected a.custom-item-link{
color: #6F1928 !important;
    background-color: #5186C0
}

.item-meta{
font-size: 14px;
color: #B5C4D5
}

p{
color: white !important;
}

li{
/* color: white !important; */
    
}

a{
color: white !important
}

.main-menu .item-li a.custom-item-link:hover{
background-color: #4A76A8;
border-radius: 10px
}

.main-menu .item-li{
padding-bottom:0;
    border-bottom: none
}

.item-selected a.custom-item-link{
background-color: #4A76A8 !important;
    border-radius:10px !important
}

i.icon{
color:white !important
}

.dropdown-item:hover, .dropdown-item:focus{
background-color: #4A76A8;
}

.card-item a.card-body, .card-item div.card-body{
background-color: black !important
}

.card-act{
background-color: #4A76A8;
}

.profile_question_form{
background-color: black;
}

.profile_question_form .form_actions{
background-color: black;
}

.profile_question_form textarea{
background-color: black
}

.btn, .primary_btn, button, .button{
border-radius: 10px
}

.repost-list{
background-color: #21364E;
color: white
}

.card-title{
color: white
}


input[type="text"], input[type="tel"], input[type="number"], input[type="url"], input[type="password"], input[type="email"], textarea{
color: white;
background-color: black !important
}


.top-header {
    position: fixed;
/*     box-shadow: none; */
/*     border: 1px solid black */
}
.content-page {
    /* position: absolute; */
    z-index: 1;
    padding-top: 50px
}
.sidebar-menu {
    position: fixed;
    padding: 0;
    width: 220px
}
.profile-content {
    position: relative;
}
.sn-content {
    position:relative;
    margin-left: 250px;
}



.col-lg-8 div.card-body{
padding: 0;
padding-bottom: 5px;

}


.cards-list{
padding:5px;
}

.card-content{
padding-left: 40px;
}

.message-item-right .card-content{
padding-right: 40px;
}

.card-date{
line-height: 0
}

.card-status-text{
margin-top: 0
}

.message-item .card-status-text{
    font-weight: 400;
    background: #4A76A8 !important;
    border-radius: 5px;
}


.card-item .classic-item a.card-body, .card-item .classic-item div.card-body, .card-item.message-item div.card-body{
background-color: black
}


span .card-status-text-message{
 color: white !important;
}

.card-item span {
color: white !important;
}

.card-icon{
height: 30px;
width: 30px
}

input[type="text"], input[type="tel"], input[type="number"], input[type="url"], input[type="password"], input[type="email"], textarea{
background-color: #1b1b1b;
    border-radius: 10px;
    border: 1px solid #2b2b2b
}
.message-item:last-child{
border-bottom: 1px solid #2b2b2b
}

.top-banner .prompt .button{
background-color: #285473;
    color: white !important
}

.card-item .touch-item.active{
background-color: #285473
}

.card-item .touch-item:hover{
background-color: #212121 !important
}

.dropdown-menu{
background-color: #212121;
    border: 2px solid #323232;
    border-radius: 10px
}

.flat_btn:hover{
background-color: #ED7B09;
    border-radius: 10px;
    border: 1px solid  #4A7652
}

.flat_btn{
background-color: #A0570E;
    border-radius: 10px;
    border: 1px solid  #A05234
}
.main-menu-action-button{
background-color: #4A76A8;
}

.main-menu .item-li{
border-radius: 10px
}

.main-menu .item-li:hover{
background-color: #4A76A8;
border-radius: 10px
}

span .text-default{
color:white !important
}

.far{
color: white
}

.ls-menu-item:hover, .ls-menu-subitem:hover, .ls-menu-item-selected, .ls-menu-item-selected:hover{
background-color: #4A76A8;
}

.settings-page .profile-content{
background-color: black;
} 
.settings-page .profile-content .title{
color: white;
} 

.form-cell h3{
color: white;
}

.standard-page .errors-container{
filter: invert(2)
}

.posts-list-page-bordered .link-preview-item{
background-color: pink;
    border-radius: 10px;
	margin-left: 15px;
    margin-right: 15px;
    margin-top: 10px
}

.comment-loader{
background-color: black
}

.comment-loader:hover{
background-color: #4A76A8
}

.media .media-time{
color: black !important
}

.text-muted{
color: white !important
}
.media .comment-bubble{
background-color: #4A76A8;
}

.card{

margin-bottom: 5px
}

.top-banner.empty-list-banner{
/* background-color: black; */
    filter: invert(2)
}
.standard-page h1{
color: white
}
.ls-menu span{
color: white !important
}

button.blue:hover, input[type="button"].blue:hover, input[type="submit"].blue:hover, .button.blue:hover{
border-radius: 10px
}

button.blue, input[type="button"].blue, input[type="submit"].blue, .button.blue{
border-radius: 10px
}

.item-li-main:hover .main-menu-action-button{
border-radius: 10px
}

@media(max-width: 992px) {
    .sn-content {
        margin-left: 0
    }
    
    .sn-sidenav{
    background-color: #2d2d2d                                    
    }
    
    .has-bottom-footer #main-footer{
    background-color: black
    }
}
}

@-moz-document url-prefix("https://web.prisga.id/account/settings/") {
.card{
    position: absolute;
    top: -350px;
    left: 900px;
    width: 230px
}

@media (max-width: 992px){
    .card{
    position: relative;
    margin-top: 0;
    top: 0;
    left: 0;
    width: 100%
	}
}

}

@-moz-document url("https://web.prisga.id/account/groups") {
.card{
    position: absolute;
    top: -350px;
    left: 900px;
    width: 230px
}



@media (max-width: 992px){
     .card{
    position: relative;
    margin-top: 0;
    top: 0;
    left: 0;
    width: 100%
	}
}
}

@-moz-document url("https://web.prisga.id/account/messages") {
.card-item.classic-item a.card-body, .card-item.classic-item div.card-body, .card-item.message-item div.card-body{
padding: 5px 15px 0 15px;
    background-color: black;
}
.card-action{
margin:0
}

.card-title{
width: 300px
}

.card-act{
background-color: #3E3E3E
}

.top-banner .prompt .button{
background-color:#4A76A8;
    color: white !important;
	border: 2px solid #335275
}
}

@-moz-document url("https://web.prisga.id/account/wall") {
.sn-content-sidebar-block{
max-width:540px
}
}
