}

.button:focus,
.button.primary:focus,
.profilePage .button:focus,
.changeUsernameUnit .button:focus,
.actionButton--sendMoney:focus,
.dottesStyle.buttonStyle:focus,
.button[href*="manage"]:focus,
.button[href*="write"]:focus {
