    font-weight: 600;
    color: rgb(130 88 248);
    content: "\f002";
    float: left;
    font-size: 15px;
    line-height: 50px;
    left: 20px;
    pointer-events: none;
    position: absolute;
}

.message .onlineMarker {
    border-radius: 50%;
    display: inline-block;
    background: rgb(130 88 248);
    width: 10px;
    height: 10px;
}

