style.css:
@-moz-document domain("lkong.com") {
/** 全局 */

/* 广告 */
.lkong-ads {
  display: none;
}

/* 缩减页眉尺寸 */
.css-hls02m,
.css-7o8ir {
	height: 40px;
}

.css-i97t1s,
.badge-wrap{
	line-height: unset;
}


/* 主区域边距精确化 */
.css-7o8ir,
.css-83qgcm{
	width: unset;
	padding: 0 80px;
	margin: unset;
}

/* 优化列表项 */
h4~div ul {
	list-style: disc !important;
}
h4~div  li::before {
	display: none !important;
}

/* 页码去掉圆圈 */
.ant-pagination-item {
	border: none;
}
}

@-moz-document url("https://www.lkong.com/"), url("https://www.lkong.com/feeds") {
/** 首页 */

/* 论坛名两列显示 */
.css-1cw8ku2~div {
	width: 700px;
  display: flex;
  flex-flow: wrap;
}

.css-1fii08f {
	width: 350px;
}

/* 热门置于ID前 */
.css-x4oy6m{
	display: flex;
	flex-direction: column-reverse;
	justify-content: flex-end;
}
}

@-moz-document url-prefix("https://www.lkong.com/forum/") {
/** 子论坛页 */

/* 论坛名放到页尾 */

.css-xdvosn {
  display: flex;
	flex-direction: column-reverse;
}

/* 发表帖子按钮置于页面顶端 */
.right .ant-btn-primary {
	position: fixed;
	top: 5px;
	left: 350px;
	z-index: 10;
	width: 120px;
	height: 30px !important;
	padding: 0;
}

.css-1xrb416{
	display:none;
}

.css-b7o9sn {
	padding: 0;
}

/* 热门置于ID前 */
.css-1oa56wn{
	display: flex;
	flex-direction: column-reverse;
	justify-content: flex-end;
}
}

@-moz-document url-prefix("https://www.lkong.com/thread/") {
/** 帖子页 */

/* 标题栏*/

.css-wd6gb6 {
	padding: 8px 0;
	display: flex;
	flex-flow: row wrap;
	justify-content: flex-end;
	align-items: center;
	width: unset;
	margin: 0 80px;
}
.thread-title {
	margin-bottom: 0;
  width: 100%;
}

.thread-title~div:last-child {
	margin-right: 0;
}

.css-116wftn{
  margin-bottom: 2px;
}

/* 左边头像栏 */
.css-1v7uwc5,
.css-iz0yxa {
	margin-right: 2px;
  min-height: unset;
  padding: 10px;
}

/* 头像 */
.css-s98rak,
.avatar-wrapper {
  height: 162px !important;
  width: 162px !important;
  border: none !important;
  border-bottom: 1px solid rgb(238, 238, 238) !important;
  border-radius: 0 !important;
}
.css-s98rak span,
.avatar-wrapper span {
  width: 100% !important;
  height: 100% !important;
}

/* ID */
.user-wrapper {
  margin: 5px !important
}


/* 跟帖信息栏 */
.css-dz4ra9 {
	padding: 3px 0;
}

/* 发表时间 */
.left-area {
	margin-left: 10px !important;
}

/* 楼号 */
.lou {
  margin-right: 10px !important;
}

/* 帖子内容 */
.main-block {
	padding: 5px 10px !important; 
}
.thread-content p {
	margin: 0;
}
/* 引用 */
.css-h0ave8 {
	padding: 5px;
}

/* 评分 */
.css-1arbysm {
  margin: 40px 10px 10px;
}
.css-1arbysm table {
	margin-top: 5px;
}
.css-1arbysm table td{
	padding: 2px;
}

/* 回复/评分/收藏/举报栏*/
.css-9ph873 {
	padding: 0 10px 5px;
}

/* 回复表单 */
.css-1mtj6bn {
	padding: 10px 10px 0;
}
}

@-moz-document url-prefix("https://www.lkong.com/new/"), url-prefix("https://www.lkong.com/edit") {
/** 发布主题页 */

.css-1fcdc3s .ant-affix {
	top: 40px !important;
}
.editor-header,
.editor-main{
	width: unset !important;
	padding: 0 80px !important;
	margin: unset !important;
}
.css-1d0y4b9 {
	padding: unset;
}

.css-1d0y4b9 button:first-child {
	margin: 0 2px 0 0;
	padding: 0 6px 0 0;
}

.editor-main {
	margin-top: 10px !important;
}

.css-9xr9bw {
  padding: 0;
}
.editor-main>div:nth-child(2) {
	padding: 10px !important;
}
}
