style.css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
CTM Forum
http://forum.cyberctm.com/forum/
*/

@-moz-document url-prefix("http://forum.cyberctm.com/forum/") {
body,td,th,div,font {
letter-spacing: 1px;
background-color:#111111 !important;
color:#eeeeee !important;
font-size:18px !important;
}
a  { color: #CCFF99 !important;}
a:link { color: #CCFF99; }
a:visited { color: #99CCFF; }
a:hover { color: #FF9999; }
a:active { color: #FF6666; }
.lighttxt, .smalltxt, .todayposts { color: #eeeeee !important; }
.header {
background-image: none !important;
} 
.p_total { background-color:#111111 !important; }
.p_pages { background-color:#111111 !important; }
.p_curpage { background-color:#111111 !important; }
.p_num { background-color:#111111 !important; }
.p_redirect { background-color:#111111 !important; }
}
