style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pixiv.net") {

/* try to hide logo and advertisement */
.title.header-logo,
._global-header [href*="premium.php"],
#logoMap,
#logo_s, 
._global-header h1,
[id^="u3m"], 
#pageAdver, 
#logoRightAdver, 
._global-header .ad,
#header-banner.ad {display: none !important;}

/* header height */
._global-header .layout-wrapper {
padding: .4em 0 !important;
height: auto !important;
}

/* remove min-width */
._global-header .category-list {
min-width: 0 !important;
}

/* === Remove position:absolute ===*/

.navigation-list,
.category-list,
.notifications  {
position: static !important; 
float: left !important;
width: auto !important;
}

.link-list {
position: static !important; 
display: inline-block !important;}

/* Blue Navigation Icons */
.navigation-list > ul > li:not(.upload):not(.notification) {

width: 20px !important; 
height: 20px  !important;
overflow: hidden !important;
padding: 0 !important;
text-indent: -9999px;
margin-right: 12px !important;
}

.navigation-list .upload {

height: 20px !important; 
overflow: hidden !important;
margin-right: 12px !important;
}

/* Notification (Rabbit and Envelope icon) */
.notifications {margin-right: 0 !important;}

._global-header .notification-popup {top: 34px !important; left: 200px !important;}

/* Event + Novel */
.category-list {margin-right: 8px !important;}

.category-list ul li {

display: inline-block !important;
margin: 0 4px 0 0 !important;
width: 28px !important;
height: 22px !important;
overflow: hidden !important;
}

/* Search box */
.ui-search {top: .3em !important;}

/* Remove styling */
.category-list,
.notifications li a {
background-color: transparent !important; 
border: none !important;
}

}
