style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(about:addons) {

  .addon:not([selected]) .icon-container,
  .addon:not([selected]) .warning,
  .addon:not([selected]) .advancedinfo-container {
    visibility: collapse !important ;
  }

  .addon:not([selected]) .name-container {
    font-size: 125% !important;
  }

  .addon:hover:not([selected]) {
    background-color: -moz-menuhover !important;
  }

  .addon:hover:not([selected]) .name {
    color: -moz-menuhovertext !important;
  }

  #userstyle-top-container {
    border-bottom: 1px solid rgb(202, 212, 224);
  }

}

/*****************************
  Roses are rgb(255, 0, 0);
  Violets are rgb(0, 0, 255);
 *****************************/
