style.css:
@-moz-document url-prefix("http://www.google.com/calendar/"), 
               url-prefix("https://www.google.com/calendar/"), 
               url-prefix("https://calendar.google.com/calendar/") {

  /* weekends on main calendar */
  .st-bg:nth-child(6), .st-bg:nth-child(7),
  .st-dtitle:nth-child(6), .st-dtitle:nth-child(7),
  /* weekends on week calendar */
  .tg-weekend {
    background-color: #E6EEF5 !important;
  }

}
