style.css:
/*****************************************
 * Gmail Toolbar Icon Labels
 *
 * Author      : @crdunst
 * Updated     : January 3rd, 2012
 * Version     : 1.1
 * Description : Overrides the mystery meat toolbar icons in the new gmail UI
 * Usage       : Use with Stylish Firefox/Chrome extension (http://userstyles.org/)
 *****************************************/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {

.zF, .bqe, .bq3 {
	font-weight: 500;
}

.T-I {
    line-height: 22px !important;
    padding-top: 3px !important;
    height: 23px !important;
}

.asa {
	width: auto !important;
	padding-right: 50px;
    text-align: left !important;
    color: #000 !important;
}
.T-I.J-J5-Ji.m9.T-I-ax7.L3 .asa,
.T-I.J-J5-Ji.bvt.T-I-ax7.L3 .asa {
	padding-right: 80px;
}
.asa::before {
	display: none;
}

.ash {
    text-align: left !important;
    color: #000 !important;
}


.ar6,.ar8,.asl,.ar9,.ase,.asb,.asf,.aos,.hB,.bAO,.bAP,.brW {
    text-indent: 22px !important;
    width: 20px !important;
}

.aos {
    margin-right: 48px;
}

.ar6:before {content:"Back";}
.ar8:before {content:"Archive";}
.asl:before {content:"Spam";}
.ar9:before {content:"Delete";}
.ase:before {content:"Move to";}
.asb:before {content:"Labels";}
.asf:before {content:"Reload";}
.aos:before {content:"Settings";}
.hB:before {content:"Reply";}
.bAO:before {content: "Mark Read";}
.bAP:before {content: "Mark Read";}
.brW:before {content: "Snooze";}


div[title="Back to Inbox"] div .ar6:before,
div[data-tooltip="Back to Inbox"] div .ar6:before {
    content:"Inbox";
}

/* remove highlight circles */
.asa:not(.bjy)::after,
.xY>.T-Jo::before {
	display: none;
}

}
